import{_ as t,c as e,o as r,a as p}from"./app.ab0ae781.js";const u=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u524D\u8A00","slug":"\u524D\u8A00","link":"#\u524D\u8A00","children":[]},{"level":2,"title":"\u53D8\u4E0E\u4E0D\u53D8","slug":"\u53D8\u4E0E\u4E0D\u53D8","link":"#\u53D8\u4E0E\u4E0D\u53D8","children":[]},{"level":2,"title":"\u524D\u7AEF\u5DE5\u7A0B\u5E08\uFF0C\u9996\u5148\u662F\u8F6F\u4EF6\u5DE5\u7A0B\u5E08","slug":"\u524D\u7AEF\u5DE5\u7A0B\u5E08\uFF0C\u9996\u5148\u662F\u8F6F\u4EF6\u5DE5\u7A0B\u5E08","link":"#\u524D\u7AEF\u5DE5\u7A0B\u5E08\uFF0C\u9996\u5148\u662F\u8F6F\u4EF6\u5DE5\u7A0B\u5E08","children":[]},{"level":2,"title":"\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B66\u4E60\u4E4B\u9053","slug":"\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B66\u4E60\u4E4B\u9053","link":"#\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B66\u4E60\u4E4B\u9053","children":[{"level":3,"title":"\u5176\u5B9E\u4E0D\u96BE","slug":"\u5176\u5B9E\u4E0D\u96BE","link":"#\u5176\u5B9E\u4E0D\u96BE","children":[]}]}],"relativePath":"design/1 \u5F00\u7BC7\uFF1A\u524D\u7AEF\u5DE5\u7A0B\u5E08\u7684\u6210\u957F\u8BBA.md"}'),o={name:"design/1 \u5F00\u7BC7\uFF1A\u524D\u7AEF\u5DE5\u7A0B\u5E08\u7684\u6210\u957F\u8BBA.md"},n=p('<p>\u5373\u4F7F\u662F\u5728\u77AC\u606F\u4E07\u53D8\u7684\u524D\u7AEF\u9886\u57DF\uFF0C\u4E5F\u5B58\u5728\u4E00\u4E9B\u5177\u5907\u201C\u4E00\u6B21\u5B66\u4E60\uFF0C\u7EC8\u751F\u53D7\u7528\u201D\u7279\u6027\u7684\u77E5\u8BC6\uFF0C\u6BD4\u5982<a href="https://juejin.cn/book/6844733750048210957/section/6844733750031417352#heading-4" target="_blank" rel="noreferrer">\u524D\u7AEF\u6027\u80FD\u4F18\u5316</a>\u7684\u6838\u5FC3\u601D\u8DEF\uFF0C\u518D\u6BD4\u5982\u6211\u4EEC\u8FD9\u6B21\u8981\u5C55\u5F00\u6765\u8BB2\u7684JavaScript\u8BBE\u8BA1\u6A21\u5F0F\u3002</p><blockquote><p>\u5728\u8F6F\u4EF6\u5DE5\u7A0B\u4E2D\uFF0C\u8BBE\u8BA1\u6A21\u5F0F\uFF08Design Pattern\uFF09\u662F\u5BF9\u8F6F\u4EF6\u8BBE\u8BA1\u4E2D\u666E\u904D\u5B58\u5728\uFF08\u53CD\u590D\u51FA\u73B0\uFF09\u7684\u5404\u79CD\u95EE\u9898\uFF0C\u6240\u63D0\u51FA\u7684\u89E3\u51B3\u65B9\u6848\u3002 \u2014\u2014\u7EF4\u57FA\u767E\u79D1</p></blockquote><p>\u70F9\u996A\u6709\u83DC\u8C31\uFF0C\u6E38\u620F\u6709\u653B\u7565\uFF0C\u6BCF\u4E2A\u9886\u57DF\u90FD\u5B58\u5728\u4E00\u4E9B\u80FD\u591F\u8BA9\u6211\u4EEC\u53C8\u597D\u53C8\u5FEB\u5730\u8FBE\u6210\u76EE\u6807\u7684\u201C\u5957\u8DEF\u201D\u3002\u5728\u7A0B\u5E8F\u4E16\u754C\uFF0C\u7F16\u7A0B\u7684\u201C\u5957\u8DEF\u201D\u5C31\u662F\u8BBE\u8BA1\u6A21\u5F0F\u3002</p><p>\u6388\u4EBA\u4EE5\u9C7C\uFF0C\u4E0D\u5982\u6388\u4EBA\u4EE5\u6E14\u3002\u5728\u5C1D\u8BD5\u5B66\u4E60\u4E00\u95E8\u77E5\u8BC6\u4E4B\u524D\uFF0C\u6700\u91CD\u8981\u7684\u4E8B\u60C5\u662F\u641E\u6E05\u695A\u8FD9\u5757\u77E5\u8BC6\u672C\u8EAB\u7684\u610F\u4E49\uFF08\u4E3A\u4EC0\u4E48\u8981\u5B66\uFF1F\uFF09\u3001\u4F5C\u7528\uFF08\u5B66\u4E86\u5B83\u80FD\u5E72\u561B\uFF1F\uFF09\u548C\u7279\u6027\uFF08\u600E\u4E48\u5B66\u6BD4\u8F83\u597D\uFF1F\uFF09\uFF0C\u4ECE\u800C\u5EFA\u7ACB\u8D77\u81EA\u5DF1\u7684\u5168\u5C40\u89C2\u3002\u8FD9\u4E2A\u8FC7\u7A0B\u548C\u77E5\u8BC6\u672C\u8EAB\u540C\u6837\u91CD\u8981\uFF0C\u4E5F\u6B63\u662F\u8FD9\u4E00\u8282\u6211\u4EEC\u8981\u7740\u91CD\u89E3\u51B3\u7684\u51E0\u4E2A\u95EE\u9898\u3002</p><p>\u6B64\u5916\uFF0C\u4F5C\u4E3A\u5C0F\u518C\u4F5C\u8005\uFF0C\u66F4\u4F5C\u4E3A\u5927\u5BB6\u7684\u540C\u884C\uFF0C\u6211\u4E5F\u60F3\u501F\u8FD9\u4E2A\u673A\u4F1A\uFF0C\u8DDF\u5927\u5BB6\u804A\u4E00\u4E9B\u66F4\u5B9E\u5728\u7684\u4E1C\u897F\uFF0C\u6BD4\u5982\u804A\u804A\u6211\u4EEC\u524D\u7AEF\u5DE5\u7A0B\u5E08\u600E\u4E48<s>\u53D8\u79C3</s>\u53D8\u5F3A\u7684\u8BDD\u9898\u3002\u8FD9\u90E8\u5206\u5185\u5BB9\u4F4D\u4E8E\u672C\u8282\u7684\u201C\u524D\u8A00\u201D\u90E8\u5206\uFF0C\u5982\u9700\u8DF3\u8BFB\uFF0C\u5927\u5BB6\u76F4\u63A5\u5F80\u4E0B\u62C9\u5230\u7B2C\u4E8C\u5C0F\u8282\u5373\u53EF\u3002</p><h2 id="\u524D\u8A00" tabindex="-1">\u524D\u8A00 <a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a></h2><p>\u4E0B\u5B9A\u51B3\u5FC3\u5199\u8FD9\u672C\u5C0F\u518C\uFF0C\u662F\u57282018\u5E74\u7684\u5E74\u672B\u3002</p><p>\u76F8\u4FE1 18 \u5E74\u4E0B\u534A\u5E74\u5230\u73B0\u5728\uFF0819\u5E74\u521D\uFF09\u8FD9\u6BB5\u65F6\u95F4\uFF0C\u5927\u5BB6\u90FD\u5BF9\u8FD9\u6837\u4E00\u4E2A\u4E8B\u60C5\u6DF1\u6709\u4F53\u4F1A\uFF1A <strong>\u8D29\u5356\u7126\u8651\u7684\u4EBA\u8D8A\u6765\u8D8A\u591A\u4E86</strong>\u3002</p><p>\u8BB8\u591A\u6280\u672F\u516C\u4F17\u53F7\u9891\u7E41\u4EE5\u5E26\u6709\u201C\u5371\u673A\u201D\u201C\u5BD2\u51AC\u201D\u8FD9\u6837\u7684\u6807\u9898 po \u6587\uFF0C\u6280\u672F\u793E\u533A\u4E5F\u5F00\u59CB\u53CD\u590D\u51FA\u73B0\u7C7B\u4F3C\u4E8E\u201C\u524D\u811A\u5BF9\u9700\u6C42\u540E\u811A\u88AB\u89E3\u7EA6\u201D\u8FD9\u6837\u4E0D\u592A\u597D\u7684\u6D88\u606F\u3002\u4F34\u968F\u8FD9\u4E9B\u58F0\u97F3\u800C\u6765\u7684\uFF0C\u662F\u540C\u884C\u4EEC\u6570\u4E0D\u6E05\u7684\u81EA\u6211\u6000\u7591\uFF1A</p><ul><li>\u4E92\u8054\u7F51\u7684\u98CE\u53E3\u8981\u6CA1\u4E86\uFF0C\u8981\u4E0D\u8981\u5148\u8D70\u4E3A\u656C\uFF1B</li><li>\u5199\u524D\u7AEF/\u5199\u540E\u7AEF/\u5199\u5BA2\u6237\u7AEF/\u5199\u7B97\u6CD5\u6CA1\u524D\u9014\u4E86\uFF0C\u8981\u4E0D\u8981\u8D81\u65E9\u8F6C\u884C\uFF1B</li><li>\u7A0B\u5E8F\u5458\u5E02\u573A\u662F\u4E0D\u662F\u5DF2\u7ECF\u9971\u548C\u4E86\uFF0C\u6211\u8FD8\u80FD\u4E0D\u80FD\u5206\u5230\u4E00\u676F\u7FB9</li><li>\xB7\xB7\xB7\xB7\xB7\xB7</li></ul><p>\u4E00\u591C\u4E4B\u95F4\uFF0C\u6709\u66F4\u591A\u7684\u4EBA\u5F00\u59CB\u601D\u8003\u662F\u5411\u524D\u8D70\u8FD8\u662F\u5411\u540E\u9000\u7684\u95EE\u9898\u3002</p><p>\u804C\u4E1A\u9009\u62E9\u4E5F\u597D\u3001\u4EBA\u751F\u89C4\u5212\u4E5F\u7F62\uFF0C\u8FD9\u4E9B\u4E1C\u897F\u90FD\u56E0\u4EBA\u800C\u5F02\uFF0C\u6BCF\u4E2A\u4EBA\u7684\u60F3\u6CD5\u90FD\u503C\u5F97\u6211\u4EEC\u53BB\u5C0A\u91CD\u3002\u54B1\u4EEC\u4ECA\u5929\u4E0D\u804A\u7126\u8651\uFF0C\u4E5F\u4E0D\u704C\u9E21\u6C64\uFF0C\u6211\u5C31\u60F3\u7B80\u5355\u5730\u548C\u5927\u5BB6\u8BF4\u8BF4\u6211\u6240\u770B\u5230\u7684\u3002</p><p>\u53EF\u80FD\u548C\u5F88\u591A\u540C\u5B66\u60F3\u7684\u4E0D\u4E00\u6837\uFF0C\u6211\u6240\u4F53\u9A8C\u5230\u7684\u73B0\u5B9E\u662F\uFF1A<strong>\u524D\u7AEF\u56E2\u961F\u771F\u7684\u5F88\u7F3A\u4EBA\uFF1B\u524D\u7AEF\uFF0C\u771F\u7684\u592A\u96BE\u62DB\u4E86\u3002</strong></p><p>\u5355\u8BF4 18 \u5E74\u8FD9\u4E00\u5E74\uFF0C\u6211\u6240\u4E86\u89E3\u5230\u7684\u524D\u56E2\u961F\u3001\u73B0\u56E2\u961F\u548C\u5144\u5F1F\u56E2\u961F\u90FD\u5728\u62DB\u4EBA\uFF1A\u7528\u4EBA\u8FD9\u4E2A\u9700\u6C42\u5C31\u6CA1\u65AD\u8FC7\u3002\u6240\u4EE5\u8BF4\u7F3A\u4EBA\uFF0C\u662F\u771F\u7F3A\uFF1B\u8BF4\u62DB\u4E0D\u5230\uFF0C\u4E5F\u662F\u771F\u62DB\u4E0D\u5230\u3002\u5F88\u591A\u60C5\u51B5\u4E0B\uFF0C\u53D7\u8BD5\u8005\u7684<strong>\u7F16\u7801\u80FD\u529B\u3001\u8BBE\u8BA1\u601D\u7EF4\u548C\u8BA1\u7B97\u673A\u57FA\u7840</strong>\u90FD\u4E0D\u90A3\u4E48\u7ECF\u5F97\u8D77\u63A8\u6572\uFF0C\u7EB5\u4F7F\u6709\u518D\u591A\u7684 HC \u4E5F\u767D\u642D\u3002\u884C\u4E1A\u6CA1\u6709\u4EE5\u524D\u90A3\u4E48\u201C\u666F\u6C14\u201D\u4E86\uFF0C\u8FD9\u4E0D\u5047\uFF0C\u4F46\u60C5\u51B5\u8FDC\u6CA1\u6709\u7CDF\u7CD5\u5230\u4EBA\u4EBA\u81EA\u5371\u7684\u7A0B\u5EA6\u3002</p><p>\u5F53\u4E0B\u7684\u8FD9\u6CE2\u5BD2\u6F6E\uFF0C\u5E26\u6765\u7684\u4E3B\u8981\u95EE\u9898\u662F\u66FE\u7ECF\u7AD9\u5728\u98CE\u53E3\u4E0A\u5C31\u80FD\u98DE\u8D77\u6765\u7684\u732A\uFF0C\u73B0\u5728\u98DE\u4E0D\u8D77\u6765\u4E86\u3002\u4F46\u6CA1\u5173\u7CFB\uFF0C\u52AA\u529B\u4FEE\u70BC\u4FEE\u70BC\uFF0C\u505A\u9E70\u5C31\u4E0D\u6015\u4E86\u3002</p><p>\u4ECE\u76EE\u524D\u6211\u6240\u4E86\u89E3\u5230\u7684\u3001\u8089\u773C\u53EF\u89C1\u7684\u5DE8\u5927\u7684\u524D\u7AEF\u5DE5\u7A0B\u5E08\u7684\u6570\u91CF\u7F3A\u53E3\u6765\u770B\uFF0C<strong>\u524D\u7AEF\u9886\u57DF\u8DDD\u79BB\u4EBA\u624D\u9971\u548C\u8FD8\u6709\u5F88\u957F\u5F88\u957F\u4E00\u6BB5\u8DEF\u8981\u8D70</strong>\u3002\u6240\u4EE5\u8BF4\u64CD\u5FC3\u884C\u4E1A\u7684\u672A\u6765\u610F\u4E49\u4E0D\u5927\uFF0C\u5927\u5BB6\u66F4\u5E94\u8BE5\u53BB\u601D\u8003\u5982\u4F55\u7EC3\u4E0B\u624E\u5B9E\u7684\u57FA\u672C\u529F\u3001\u8FC7\u786C\u7684\u4E1A\u52A1\u7D20\u8D28\u548C\u7075\u6D3B\u7684\u9002\u914D\u80FD\u529B\uFF0C\u8FD9\u6837\u5C31\u80FD\u4E0D\u754F\u53D8\u5316\uFF08\u5C0F\u518C\u7B2C\u4E8C\u8282\u6211\u4EEC\u4F1A\u63D0\u5230\uFF0C\u8BBE\u8BA1\u6A21\u5F0F\u7684\u6839\u672C\u76EE\u7684\u662F\u4E3A\u4E86\u4F7F\u6211\u4EEC\u7684\u4EE3\u7801\u5177\u5907\u66F4\u5F3A\u7684<strong>\u5E94\u5BF9\u53D8\u5316</strong>\u7684\u80FD\u529B\uFF09\u3002</p><p>\u9762\u5BF9\u53D8\u5E7B\u83AB\u6D4B\u7684\u9700\u6C42\uFF0C\u4EE3\u7801\u5C1A\u4E14\u670923\u5F0F\uFF0C\u66F4\u4F55\u51B5\u6211\u4EEC\u5199\u4EE3\u7801\u7684\u4EBA\u5462\uFF1F\uFF09\u3002</p><p>\u8BDD\u8BF4\u56DE\u6765\uFF0C\u6709\u7F3A\u53E3\u5374\u62DB\u4E0D\u5230\u4EBA\uFF0C\u8FD9\u662F\u4E2A\u975E\u5E38\u9EBB\u70E6\u7684\u4E8B\u60C5\u3002\u8FD9\u4E00\u70B9\uFF0C\u9664\u4E86\u9700\u6C42\u65B9\u548C\u53D7\u8BD5\u8005\u53CC\u65B9\u7684\u95EE\u9898\uFF0C\u548C\u524D\u7AEF\u8FD9\u4E2A\u884C\u4E1A\u7684\u7279\u6027\u4E5F\u6709\u7740\u5BC6\u4E0D\u53EF\u5206\u7684\u5173\u7CFB\u3002</p><p>\u6211\u77E5\u9053\u5F88\u591A\u540C\u5B66\u5165\u884C\u5DEE\u4E0D\u591A\u662F\u8FD9\u6837\u7684\u987A\u5E8F\uFF1AJavaScript \u57FA\u7840-&gt; jQuery\uFF08\u73B0\u5728\u5F88\u591A\u4EBA\u53EF\u80FD\u6CA1\u5199\u8FC7 jQuery\uFF09-&gt;\u70ED\u95E8\u6846\u67B6-&gt;\u4EFF\u7AD9-&gt;\u627E\u5DE5\u4F5C\u3002\u9996\u5148\u8981\u8BF4\u8FD9\u4E2A\u8FC7\u7A0B\u5B83\u6CA1\u6709\u9519\uFF0C\u524D\u7AEF\u8FD9\u95E8\u8BFE\u5927\u5B66\u4E0D\u5F00\uFF0C\u4E5F\u4E0D\u5B58\u5728\u4EC0\u4E48\u89C4\u8303\u7684\u7CFB\u7EDF\u5316\u6559\u7A0B\u80FD\u591F\u4F5C\u4E3A\u91D1\u79D1\u7389\u5F8B\uFF0C\u5927\u5BB6\u90FD\u662F\u91CE\u8DEF\u5B50\u51FA\u8EAB\uFF0C\u91CE\u8DEF\u5B50\u5165\u884C\u4E0D\u8FD9\u4E48\u641E\u8FD8\u80FD\u600E\u4E48\u641E\uFF1F\u80FD\u591F\u628A\u8FD9\u6837\u4E00\u4E2A\u8FC7\u7A0B\u5B8C\u5B8C\u6574\u6574\u8D70\u4E0B\u6765\u7684\u540C\u5B66\uFF0C\u8DB3\u4EE5\u8BC1\u660E\u5176\u5BF9\u7F16\u7A0B\u7684\u5174\u8DA3\u548C\u591F\u683C\u7684\u6267\u884C\u529B\uFF01\u6240\u4EE5\u8BF4\u8FD9\u4E48\u641E\u6CA1\u9519\uFF0C<strong>\u9519\u7684\u662F\u628A\u8FD9\u4E9B\u4E1C\u897F\u5F53\u4F5C\u4E86\u4E00\u4E2A\u5408\u683C\u524D\u7AEF\u9700\u8981/\u5E94\u8BE5\u638C\u63E1\u7684\u5168\u90E8</strong>\u3002</p><h2 id="\u53D8\u4E0E\u4E0D\u53D8" tabindex="-1">\u53D8\u4E0E\u4E0D\u53D8 <a class="header-anchor" href="#\u53D8\u4E0E\u4E0D\u53D8" aria-hidden="true">#</a></h2><p>\u524D\u7AEF\u8FD9\u4E2A\u804C\u4E1A\u786E\u5B9E\u4E0D\u8F7B\u677E\uFF0C\u5C24\u5176\u662F\u79FB\u52A8\u4E92\u8054\u7F51\u5927\u70ED\u7684\u8FD9\u51E0\u5E74\uFF0C\u6280\u672F\u9769\u547D\u5C31\u6CA1\u6D88\u505C\u8FC7\u3002\u5355\u8BF4\u6846\u67B6/\u5E93\u5427\uFF0C\u6211\u76F8\u4FE1\u5C4F\u5E55\u524D\u4E00\u5B9A\u6709\u8DDF\u6211\u4E00\u6837\u4ECE\u539F\u751F JavaScript \u5F00\u59CB\u5199\uFF0C\u7136\u540E\u5199 jQuery\u3001\u5199 zepto\u3001\u5199 Angular\uFF0C\u5199\u5199\u5199\uFF0C\u4E00\u76F4\u5199\u5230\u73B0\u5728\u7684 Vue/React \u7B49\u7B49\u3002\u5C31\u7B97\u662F\u521A\u521A\u5165\u884C\u3001\u6CA1\u6709\u7ECF\u5386\u8FC7\u6280\u672F\u53D8\u9769\u6467\u6B8B\u7684\u7684\u65B0\u540C\u5B66\uFF0C\u4E5F\u80FD\u88AB\u5F53\u4E0B\u7EB7\u7E41\u590D\u6742\u7684\u524D\u7AEF\u751F\u6001\u7ED9\u8FF7\u4E86\u773C\u3002\u5F88\u591A\u540C\u5B66\u521A\u521A\u638C\u63E1\u4E86\u4E00\u95E8\u6280\u672F\uFF0C\u4EE5\u4E3Asettle down\u4E86\u3001\u53EF\u4EE5\u6C89\u6DC0\u4E00\u4E0B\u4E86\uFF0C\u7ED3\u679C\u53C8\u5192\u51FA\u4E00\u4E2A\u65B0\u73A9\u610F\u513F\uFF0C\u6CA1\u529E\u6CD5\uFF0C\u7EE7\u7EED\u8FFD\uFF01\u6240\u4EE5\u8BF4\u6211\u6CA1\u89C1\u8FC7\u54EA\u4E2A\u524D\u7AEF\u5199\u4EE3\u7801\u3001\u5B66\u4E1C\u897F\u662F\u4E0D\u5356\u529B\u7684\uFF0C\u56E0\u4E3A\u4E0D\u5356\u529B\u65E9\u6389\u961F\u4E86\u3002</p><p>\u4F46\u5927\u5BB6\u73B0\u5728\u9700\u8981\u51B7\u9759\u4E0B\u6765\u601D\u8003\u8FD9\u4E48\u4E00\u4E2A\u95EE\u9898\uFF1A\u6211\u5F88\u62FC\uFF0C\u522B\u4EBA\u4E5F\u5F88\u62FC\uFF0C<strong>\u6240\u6709\u4EBA\u90FD\u5728\u62FC\u7684\u65F6\u5019\uFF0C\u6211\u7279\u522B\u7684\u5730\u65B9\u3001\u6216\u8005\u51C6\u786E\u5730\u8BF4\u2014\u2014\u6211\u7684\u6838\u5FC3\u7ADE\u4E89\u529B\u5728\u54EA\u91CC</strong>\uFF1F</p><p>\u80FD\u591F\u51B3\u5B9A\u4E00\u4E2A\u524D\u7AEF\u5DE5\u7A0B\u5E08\u7684\u672C\u8D28\u7684\uFF0C\u4E0D\u662F\u90A3\u4E9B\u77AC\u606F\u4E07\u53D8\u7684\u6280\u672F\u70B9\uFF0C\u800C\u662F\u90A3\u4E9B<strong>\u4E0D\u53D8\u7684\u4E1C\u897F</strong>\u3002</p><p>\u4EC0\u4E48\u662F\u4E0D\u53D8\u7684\u4E1C\u897F\uFF1F</p><p>\u6211\u7ED9\u5927\u5BB6\u4E3E\u4E2A\u4F8B\u5B50\u3002\u4E24\u5E74\u524D\u67D0\u5382\u6709\u4E2A\u56E2\u961F\u53D1\u4E86\u4E00\u5219\u62DB\u8058\u5E7F\u544A\uFF0C\u8BF4\u91CD\u91D1\u6C42 Node \u73A9\u5BB6\uFF0C\u613F\u610F\u7ED9\u5F88\u9AD8\u7684 P \u7EA7\uFF0C\u85AA\u8D44\u4EC0\u4E48\u7684\u4E0D\u7528\u8BF4\u81EA\u7136\u975E\u5E38\u4E30\u539A\u3002\u4F46\u662F\u6CA1\u8FC7\u51E0\u5929\uFF0C\u8FD9\u5219\u62DB\u8058\u4FE1\u606F\u7684\u622A\u56FE\u7ADF\u7136\u51FA\u73B0\u5728\u4E86\u4E00\u4E2A\u57F9\u8BAD\u673A\u6784\u7684\u62DB\u751F\u5E7F\u544A\u91CC\uFF0C\u9644\u7684\u6587\u6848\u662F\u201C BAT \u7838 xx \u4E07\u62DB Node \u5DE5\u7A0B\u5E08\uFF0C\u800C\u4F60\u5374\u8FD8\u5728\u5B66 PHP\uFF1F\uFF01\u201D\u3002\u8FD9\u4E8B\u513F\u4F5C\u4E3A\u4E00\u4E2A\u6BB5\u5B50\u6D41\u4F20\u4E86\u5F88\u4E45\uFF0C\u56E0\u4E3A\u5B83\u8DB3\u591F\u6ED1\u7A3D\u2014\u2014\u5927\u5BB6\u60F3\u60F3\uFF0CNode \u7684\u8BED\u6CD5\u96BE\u4E0D\u96BE\uFF1F\u4E0D\u96BE\uFF0C\u51C6\u786E\u5730\u8BF4\uFF0C\u8DB3\u591F\u53CB\u597D\u4E86\uFF01\u6211\u76F8\u4FE1\u6BCF\u4E2A\u56E2\u961F\u90FD\u4E0D\u7F3A\u4F1A\u7528 Node\uFF0C\u6216\u8005\u8BF4\u7528\u4EFB\u4F55\u4E00\u95E8\u8BED\u8A00\u53BB\u5199 Hello World \u7684\u540C\u5B66\uFF0C\u7F3A\u7684\u662F\u80FD\u591F\u9A7E\u9A6D\u8FD9\u95E8\u8BED\u8A00\uFF0C\u80FD\u51ED\u501F\u81EA\u5DF1\u6DF1\u523B\u7684\u67B6\u6784\u601D\u60F3\u548C\u5DE5\u7A0B\u601D\u60F3\u53BB\u652F\u914D\u8FD9\u95E8\u8BED\u8A00\uFF0C\u5229\u7528\u5B83\u53BB\u521B\u9020\u725B\u903C\u4EA7\u51FA\u7684\u4EBA\u3002</p><p>\u6240\u8C13\u201C\u4E0D\u53D8\u7684\u4E1C\u897F\u201D\uFF0C\u8BF4\u7684\u5C31\u662F\u8FD9\u79CD<strong>\u9A7E\u9A6D\u6280\u672F\u7684\u80FD\u529B</strong>\u3002</p><p>\u5177\u4F53\u6765\u8BF4\uFF0C\u5B83\u5206\u4E3A\u4EE5\u4E0B\u4E09\u4E2A\u5C42\u6B21\uFF1A</p><ul><li>\u80FD\u7528\u5065\u58EE\u7684\u4EE3\u7801\u53BB\u89E3\u51B3\u5177\u4F53\u7684\u95EE\u9898\uFF1B</li><li>\u80FD\u7528\u62BD\u8C61\u7684\u601D\u7EF4\u53BB\u5E94\u5BF9\u590D\u6742\u7684\u7CFB\u7EDF\uFF1B</li><li>\u80FD\u7528\u5DE5\u7A0B\u5316\u7684\u601D\u60F3\u53BB\u89C4\u5212\u66F4\u5927\u89C4\u6A21\u7684\u4E1A\u52A1\u3002</li></ul><p>\u8FD9\u4E09\u79CD\u80FD\u529B\u5728\u4F60\u7684\u6210\u957F\u8FC7\u7A0B\u4E2D\u662F\u5C42\u5C42\u9012\u8FDB\u7684\u5173\u7CFB\uFF0C\u5176\u4E2D\u540E\u4E24\u79CD\u80FD\u529B\u53EF\u4EE5\u8BF4\u662F\u5BF9\u67B6\u6784\u5E08\u7684\u8981\u6C42\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u5728\u6211\u5165\u884C\u4EE5\u6765\u63A5\u89E6\u8FC7\u7684\u5DE5\u7A0B\u5E08\u91CC\uFF0C\u80FD\u505A\u5230\u7B2C\u4E00\u70B9\u5E76\u4E14\u628A\u5B83\u505A\u5230\u624E\u5B9E\u3001\u505A\u5230\u5A34\u719F\u7684\u4EBA\uFF0C\u5DF2\u7ECF<strong>\u582A\u79F0\u540C\u8F88\u6977\u6A21</strong>\u3002</p><h2 id="\u524D\u7AEF\u5DE5\u7A0B\u5E08\uFF0C\u9996\u5148\u662F\u8F6F\u4EF6\u5DE5\u7A0B\u5E08" tabindex="-1">\u524D\u7AEF\u5DE5\u7A0B\u5E08\uFF0C\u9996\u5148\u662F\u8F6F\u4EF6\u5DE5\u7A0B\u5E08 <a class="header-anchor" href="#\u524D\u7AEF\u5DE5\u7A0B\u5E08\uFF0C\u9996\u5148\u662F\u8F6F\u4EF6\u5DE5\u7A0B\u5E08" aria-hidden="true">#</a></h2><blockquote><p>\u57FA\u7840\u7406\u8BBA\u77E5\u8BC6\u662F\u4E00\u4E2A\u4EBA\u7684\u57FA\u7EBF\uFF0C\u7406\u8BBA\u8D8A\u5F3A\u57FA\u7EBF\u8D8A\u9AD8\u3002\u518D\u4E3A\u81EA\u5DF1\u5B9A\u4E00\u4E2A\u76EE\u6807\u548C\u5411\u4E0A\u6500\u9644\u7684\u9636\u68AF\uFF0C\u90A3\u4E48\u8FBE\u5230\u76EE\u6807\u5C31\u662F\u65F6\u95F4\u95EE\u9898\uFF0C\u800C\u5F88\u591A\u91CE\u8DEF\u5B50\u5DE5\u7A0B\u5E08\u641E\u4E86\u534A\u8F88\u5B50\u4E5F\u672A\u8FBE\u5230\u4F18\u79C0\u5DE5\u7A0B\u5E08\u7684\u57FA\u7EBF\uFF0C\u5F88\u591A\u4ED6\u4EEC\u7EDE\u5C3D\u8111\u6C41\u5F97\u51FA\u7684\u9AD8\u6DF1\u5B66\u95EE\uFF0C\u4E0D\u8FC7\u662F\u6B63\u89C4\u5DE5\u7A0B\u5E08\u770B\u8D77\u6765\u5F88\u81EA\u7136\u7684\u4E1C\u897F\u3002\u2014\u2014 \u5434\u519B</p></blockquote><p>\u8FC7\u53BB\uFF0C\u4EBA\u4EEC\u5BF9\u8F6F\u4EF6\u5DE5\u7A0B\u7684\u7406\u89E3\u6BD4\u8F83\u72ED\u9698\uFF0C\u8BA4\u4E3A\u524D\u7AEF\u5C31\u662F\u9875\u9762\uFF0C\u548C\u8F6F\u4EF6\u662F\u4E24\u56DE\u4E8B\u513F\u3002\u968F\u7740\u524D\u7AEF\u5E94\u7528\u590D\u6742\u5EA6\u7684\u65E5\u65B0\u6708\u5F02\uFF0C\u5982\u4ECA\u7684\u524D\u7AEF\u5E94\u7528\u4E5F\u59A5\u59A5\u5730\u6210\u4E3A\u4E86\u8F6F\u4EF6\u601D\u60F3\u7684\u4E00\u79CD\u8F7D\u4F53\uFF0C\u800C\u524D\u7AEF\u5DE5\u7A0B\u5E08\uFF0C\u4E5F\u88AB\u8981\u6C42\u5728\u638C\u63E1\u591A\u91CD\u4E13\u4E1A\u6280\u80FD\u4E4B\u4F59\uFF0C\u5177\u5907\u6700\u57FA\u672C\u7684\u8F6F\u4EF6\u7406\u8BBA\u77E5\u8BC6\u3002</p><p>\u6280\u672F\u4EBA\u4E4B\u95F4\u7684\u53E3\u6C34\u6218\uFF0C\u6BCF\u6B21\u4F46\u51E1\u60F3\u4E0A\u5347\u4E00\u70B9\u9AD8\u5EA6\uFF0C\u4FBF\u8981\u62FF\u201D\u67B6\u6784\u201C\u8FD9\u6837\u9AD8\u5927\u4E0A\u7684\u8BDD\u9898\u51FA\u6765\u6643\u6643\u773C\u3002\u4F46\u4E8B\u5B9E\u4E0A\uFF0C<strong>\u5F88\u591A\u4EBA\u7F3A\u4E4F\u7684\u5E76\u4E0D\u662F\u8FD9\u79CD\u9AD8\u77BB\u8FDC\u77A9\u7684\u6FC0\u60C5\uFF0C\u800C\u662F\u6211\u4EEC\u524D\u9762\u63D0\u5230\u7684\u201C\u4E0D\u53D8\u80FD\u529B\u201D\u4E2D\u6700\u57FA\u672C\u7684\u90A3\u4E00\u70B9\u2014\u2014\u7528\u5065\u58EE\u7684\u4EE3\u7801\u53BB\u89E3\u51B3\u5177\u4F53\u7684\u95EE\u9898\u7684\u80FD\u529B</strong>\u3002\u8FD9\u4E2A\u80FD\u529B\u5728\u8F6F\u4EF6\u5DE5\u7A0B\u9886\u57DF\u6240\u5BF9\u6807\u7684\u7ECF\u5178\u77E5\u8BC6\u4F53\u7CFB\uFF0C\u6070\u6070\u5C31\u662F\u8BBE\u8BA1\u6A21\u5F0F\u3002</p><p>\u6240\u4EE5\u8BF4\uFF0C<strong>\u60F3\u505A\u9760\u8C31\u5F00\u53D1\uFF0C\u5148\u638C\u63E1\u8BBE\u8BA1\u6A21\u5F0F</strong>\u3002</p><h2 id="\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B66\u4E60\u4E4B\u9053" tabindex="-1">\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B66\u4E60\u4E4B\u9053 <a class="header-anchor" href="#\u8BBE\u8BA1\u6A21\u5F0F\u7684\u5B66\u4E60\u4E4B\u9053" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u6574\u672C\u5C0F\u518C\u7684\u77E5\u8BC6\u4F53\u7CFB\u4E0E\u683C\u5C40\uFF0C\u7528\u601D\u7EF4\u5BFC\u56FE\u5C55\u793A\u5982\u4E0B\uFF1A</p><p><img src="https://p1-jj.byteimg.com/tos-cn-i-t2oaga2asx/gold-user-assets/2019/5/13/16ab0c39cfa5125d~tplv-t2oaga2asx-zoom-in-crop-mark:3024:0:0:0.awebp" alt="img"></p><p>\u201C\u6A58\u751F\u6DEE\u5357\u5219\u4E3A\u6A58,\u6A58\u751F\u6DEE\u5317\u5219\u4E3A\u67B3\u201D\u2014\u2014\u4E00\u4E9B\u5728\u670D\u52A1\u7AEF\u5E94\u7528\u573A\u666F\u4E0B\u770B\u4F3C\u5408\u7406\u3001\u597D\u7528\u53C8\u9177\u70AB\u7684\u64CD\u4F5C\uFF0C\u751F\u642C\u786C\u5957\u5230\u524D\u7AEF\u7684\u573A\u666F\u91CC\u53EF\u80FD\u5C31\u4F1A\u5F04\u5DE7\u6210\u62D9\u3002\u672C\u518C\u7684\u76EE\u7684\u5E76\u4E0D\u662F\u505A\u4F20\u7EDF\u8BBE\u8BA1\u6A21\u5F0F\u4E66\u7C4D\u7684\u201C\u8BD1\u672C\u201D\uFF0C\u800C\u662F\u9762\u5411\u524D\u7AEF\u5DE5\u7A0B\u5E08\uFF0C\u8BB2\u6709\u5229\u4E8E\u524D\u7AEF\u7684\u6280\u672F\u3002\u56E0\u6B64\u5728\u6B63\u5F0F\u7684\u5B9E\u6218\u7AE0\u8282\u91CC\uFF0C\u6211\u4EEC\u6743\u8861\u6BCF\u79CD\u6A21\u5F0F\u5BF9\u524D\u7AEF\u7684\u4EF7\u503C\u3001\u5BF9 23 \u79CD\u8BBE\u8BA1\u6A21\u5F0F\u505A\u4E86\u53D6\u820D\uFF0C\u4FDD\u7559\u4E0B\u6765\u7684\u8FD9\u4E9B\u8BBE\u8BA1\u6A21\u5F0F\uFF0C\u5177\u5907\u8FD9\u4E24\u4E2A\u5171\u6027\uFF1A</p><ol><li>\u524D\u7AEF\u80FD\u7528\uFF0C\u800C\u4E14\u597D\u7528\uFF1B</li><li>\u9762\u8BD5\u4F1A\u8003\uFF0C\u800C\u4E14\u5E38\u8003\u3002</li></ol><p>\u901A\u8FC7\u5B66\u4E60\u8FD9\u90E8\u5206\u8BBE\u8BA1\u6A21\u5F0F\uFF0C\u6211\u4EEC\u81F3\u5C11\u53EF\u4EE5\u8FBE\u5230\u4E09\u4E2A\u76EE\u7684\uFF1A</p><ol><li>\u5145\u5206\u7406\u89E3\u524D\u7AEF\u8BBE\u8BA1\u6A21\u5F0F\u7684\u6838\u5FC3\u601D\u60F3\u548C\u57FA\u672C\u7406\u5FF5\uFF0C\u5728\u5177\u4F53\u7684\u573A\u666F\u4E2D\u638C\u63E1\u62BD\u8C61\u7684\u8BBE\u8BA1\u539F\u5219</li><li>\u4F1A\u5199\u4EE3\u7801\uFF0C\u4F1A\u5199\u597D\u4EE3\u7801\uFF1B</li><li>\u4F1A\u9762\u8BD5\uFF0C\u80FD\u8A00\u4E4B\u6709\u7269\u3002</li></ol><h3 id="\u5176\u5B9E\u4E0D\u96BE" tabindex="-1">\u5176\u5B9E\u4E0D\u96BE <a class="header-anchor" href="#\u5176\u5B9E\u4E0D\u96BE" aria-hidden="true">#</a></h3><p>\u8BBE\u8BA1\u6A21\u5F0F\u7684\u201C\u96BE\u201D\uFF0C\u5728\u4E8E\u5176\u4EE4\u4EBA\u671B\u800C\u751F\u754F\u7684<strong>\u62BD\u8C61\u6027</strong>\u548C\u77E5\u8BC6\u70B9\u7684<strong>\u5206\u6563\u6027</strong>\u3002\u8FD9\u5E26\u6765\u4E86\u672C\u518C\u8981\u7740\u91CD\u89E3\u51B3\u7684\u95EE\u9898\u2014\u2014<strong>\u5E2E\u52A9\u5927\u5BB6\u6446\u8131\u67AF\u71E5\u4E4F\u5473\u7684\u6280\u672F\u6050\u60E7\u611F</strong>\u3002</p><p>\u62BD\u8C61\u6027\u51E0\u4E4E\u662F\u6240\u6709\u7406\u8BBA\u6027\u77E5\u8BC6\u5171\u6709\u7684\u7279\u6027\uFF0C\u5B83\u5E26\u6765\u6700\u76F4\u89C2\u7684\u95EE\u9898\u5C31\u662F\u53EF\u80FD\u4E00\u6BB5\u8BDD\u4F60<strong>\u6BCF\u4E2A\u5B57\u90FD\u8BA4\u8BC6\uFF0C\u4F46\u8FDE\u5728\u4E00\u8D77\u4E0D\u77E5\u9053\u5B83\u5728\u8BF4\u5565</strong>\uFF1A\uFF09\u3002\u4E8E\u662F\u4EA7\u751F\u4E86\u201C\u8FD9\u5757\u77E5\u8BC6\u770B\u8D77\u6765\u597D\u725B\u903C\uFF0C\u6211\u4E00\u5B9A\u5B66\u4E0D\u4F1A\u5427\u201D\u8FD9\u6837\u7684\u9519\u89C9\u3002</p><p>\u5176\u5B9E\u8BBE\u8BA1\u6A21\u5F0F\u5E76\u4E0D\u9AD8\u5927\u4E0A\uFF0C\u5B83\u662F\u4E00\u4E2A\u975E\u5E38\u63A5\u5730\u6C14\u3001\u975E\u5E38\u5B9E\u9645\u7684\u4E1C\u897F\u2014\u2014\u56E0\u4E3A\u5B83\u672C\u8EAB\u5C31\u662F\u4E00\u5E2E\u975E\u5E38\u82E6\u903C\u7684\u7A0B\u5E8F\u5458\u5728\u81EA\u5DF1\u7684\u804C\u4E1A\u751F\u6DAF\u91CC\u5B9E\u6253\u5B9E\u5730\u8E29\u5751\u8E29\u51FA\u6765\u7684\u3002\u89E3\u51B3\u77E5\u8BC6\u62BD\u8C61\u6027\u5E26\u6765\u7684\u7406\u89E3\u969C\u788D\uFF0C\u91CD\u8981\u7684\u4E0D\u662F\u53CD\u590D\u7684\u9648\u8FF0\u3001\u89E3\u91CA\uFF0C\u800C\u662F<strong>\u628A\u81EA\u5DF1\u653E\u5230\u4E00\u4E2A\u6B63\u786E\u7684\u573A\u666F\u91CC\uFF0C\u53BB\u4F53\u4F1A\u8FD9\u4E2A\u6A21\u5F0F\u7684\u597D</strong>\u3002\u5728\u5B66\u4E60\u5177\u4F53\u8BBE\u8BA1\u6A21\u5F0F\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u6211\u4EEC\u6BCF\u4E2A\u7AE0\u8282\u90FD\u4EE5\u539F\u7406-&gt;\u5B9E\u8DF5-&gt;\u603B\u7ED3\u8FD9\u6837\u7684\u6D41\u7A0B\u6765\u8D70\uFF0C\u4E5F\u5E0C\u671B\u5927\u5BB6\u4E0D\u8981\u968F\u610F\u8DF3\u8BFB\uFF0C\u786E\u4FDD\u81EA\u5DF1\u4E0D\u4EC5\u662F\u8DDF\u7740\u770B\u4E86\uFF0C\u66F4\u662F\u8DDF\u7740\u505A\u4E86\u3002\u8BBE\u8BA1\u6A21\u5F0F\u8BF4\u8D77\u6765\u662F\u7406\u8BBA\u77E5\u8BC6\uFF0C\u4F46\u5B83<strong>\u6BD5\u7ADF\u662F\u4EBA\u4EEC\u5728\u5B9E\u8DF5\u8FC7\u7A0B\u4E2D\u603B\u7ED3\u3001\u63D0\u70BC\u51FA\u6765\u7684</strong>\uFF0C\u638C\u63E1\u5B83\u7684\u610F\u4E49\uFF0C\u6B63\u662F\u4E3A\u4E86\u628A\u5B83<strong>\u8FD8\u539F\u5230\u6211\u4EEC\u65E5\u5E38\u7684\u5B9E\u8DF5\u4E2D\u53BB</strong>\u3002</p><p>\u5206\u6563\u6027\u5219\u662F\u56E0\u4E3A\u8BBE\u8BA1\u6A21\u5F0F\u672C\u8EAB\u5C31\u662F\u4E00\u5957<strong>\u89E3\u51B3\u4E0D\u540C\u95EE\u9898</strong>\u7684\u65B9\u6848\u7684\u96C6\u5408\uFF0C\u8FD9\u4E9B\u65B9\u6848\u4E4B\u95F4\u4E4D\u4E00\u770B\u597D\u50CF\u6CA1\u6709\u4EC0\u4E48\u5173\u8054\uFF0C\u6545\u800C\u5F88\u5BB9\u6613\u4F7F\u5B66\u4E60\u8005\u9677\u5165\u8FB9\u5B66\u8FB9\u5FD8\u7684\u7A98\u5883\u3002</p><p>\u4F46\u6240\u8C13\u201C\u5206\u6563\u6027\u201D\u5176\u5B9E\u4E5F\u662F\u7EB8\u8001\u864E\u2014\u2014\u6DF1\u5165\u4E86\u89E3\u8BBE\u8BA1\u6A21\u5F0F\u540E\uFF0C\u5927\u5BB6\u4F1A\u53D1\u73B0\u6A21\u5F0F\u4E0E\u6A21\u5F0F\u95F4\u5B58\u5728\u7740\u4E0D\u53EF\u5FFD\u7565\u7684\u5171\u6027\u4E0E\u5173\u8054\u2014\u2014\u5728\u4E0B\u4E00\u5C0F\u8282\u300A\u8BBE\u8BA1\u6A21\u5F0F\u7684\u9053\u4E0E\u672F\u300B\u4E2D\uFF0C\u6211\u4EEC\u5C06\u4F1A\u5B66\u4E60\u8BBE\u8BA1\u6A21\u5F0F\u4E2D\u51E0\u4E2A\u91CD\u8981\u7684\u8BBE\u8BA1\u539F\u5219\u548C\u6838\u5FC3\u7684\u8BBE\u8BA1\u601D\u60F3\uFF1B\u63A5\u4E0B\u6765\u5B66\u4E60\u5177\u4F53\u7684\u8BBE\u8BA1\u6A21\u5F0F\u65F6\uFF0C\u5C0F\u518C\u4F1A\u5728\u5177\u4F53\u7684\u5E94\u7528\u573A\u666F\u91CC\u628A\u8FD9\u4E9B\u8BBE\u8BA1\u539F\u5219\u63B0\u788E\u56BC\u70C2\u4E86\u8FD8\u539F\u7ED9\u5927\u5BB6\u3002\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\u5927\u5BB6\u4F1A\u53D1\u73B0\uFF0C\u4E0D\u540C\u7684\u8BBE\u8BA1\u6A21\u5F0F\u5E76\u975E\u662F\u4E00\u5EA7\u5EA7\u7684\u5B64\u5C9B\uFF0C\u4ED6\u4EEC\u4E4B\u95F4\u5F7C\u6B64\u547C\u5E94\u3001\u76F8\u4E92\u6210\u5C31\uFF0C\u5171\u540C\u6784\u5EFA\u8D77\u4E86\u4E00\u5957\u5B8C\u6574\u800C\u7ECF\u5178\u7684\u8F6F\u4EF6\u601D\u60F3\u4F53\u7CFB\u3002</p><p>\u6B64\u5916\uFF0C\u8BBE\u8BA1\u6A21\u5F0F\u4E2D\u6709\u51E0\u4E2A\u7279\u522B\u91CD\u8981\u3001\u7279\u522B\u597D\u4F7F\u3001\u7279\u522B\u53D7\u9762\u8BD5\u5B98\u5173\u6CE8\u7684\u7684\uFF0C\u6211\u4EEC\u5728\u8BB2\u89E3\u7684\u8FC7\u7A0B\u4E2D\u4F1A\u6709\u9488\u5BF9\u6027\u5730\u7A7F\u63D2\u4E00\u4E9B<strong>\u9AD8\u9891\u9762\u8BD5\u771F\u9898</strong>\uFF08\u6CE8\u610F\u9762\u8BD5\u9898\u4E0D\u4E00\u5B9A\u4F1A\u5355\u5F00\u5C0F\u8282\uFF0C\u6709\u7684\u9762\u8BD5\u9898\u5C31\u7A7F\u63D2\u4E8E\u539F\u7406\u8BB2\u89E3\u4E4B\u4E2D~\uFF09\u3002\u5177\u4F53\u662F\u54EA\u51E0\u4E2A\uFF0C\u53EF\u80FD\u8981\u7B49\u5927\u5BB6\u8BFB\u5230\u4E86\u90A3\u4E00\u8282\u624D\u77E5\u9053\u4E86\u54C8\u54C8\uFF08\u6240\u4EE5\u4E0D\u8981\u968F\u4FBF\u8DF3\u8BFB\uFF1A\uFF09\uFF09\u3002</p><p>\u8BBE\u8BA1\u6A21\u5F0F\u4E2D\u6700\u6838\u5FC3\u7684\u7406\u5FF5\u548C\u601D\u60F3\u6709\u54EA\u4E9B\uFF0C\u5982\u4F55\u5C06\u8BBE\u8BA1\u6A21\u5F0F\u4ECE\u4F20\u7EDF\u7684 C++\u3001Java \u8BED\u8A00\u8FC1\u79FB\u5230 JavaScript\u3001\u4ECE\u670D\u52A1\u7AEF\u4E1A\u52A1\u573A\u666F\u8FC1\u79FB\u5230\u73B0\u4EE3\u5316\u7684\u524D\u7AEF\u5E94\u7528\u573A\u666F\uFF0C\u5982\u4F55\u628A\u63E1\u672C\u4E66\u7684\u91CD\u70B9\u96BE\u70B9 \u2014\u2014 \u5728\u8FDB\u5165\u5B9E\u6218\u73AF\u8282\u4E4B\u524D\uFF0C\u6211\u4EEC\u9996\u5148\u9700\u8981\u8D70\u8FDB\u300A\u8BBE\u8BA1\u6A21\u5F0F\u7684\u9053\u4E0E\u672F\u300B\u4E2D\uFF0C\u5BF9\u4EE5\u4E0A\u95EE\u9898\u4E00\u7AA5\u7A76\u7ADF\u3002</p>',49),s=[n];function a(l,i,g,d,c,h){return r(),e("div",null,s)}const f=t(o,[["render",a]]);export{u as __pageData,f as default};
