import{_ as p,c as t,o as e,a as r}from"./app.3aab1824.js";const h=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4E3A\u4EC0\u4E48\u8BF4 TypeScript \u53EF\u80FD\u8D85\u8D8A JavaScript\uFF1F","slug":"\u4E3A\u4EC0\u4E48\u8BF4-typescript-\u53EF\u80FD\u8D85\u8D8A-javascript\uFF1F","link":"#\u4E3A\u4EC0\u4E48\u8BF4-typescript-\u53EF\u80FD\u8D85\u8D8A-javascript\uFF1F","children":[]},{"level":2,"title":"\u5982\u4F55\u7CFB\u7EDF\u5B66\u4E60 TypeScript\uFF1F","slug":"\u5982\u4F55\u7CFB\u7EDF\u5B66\u4E60-typescript\uFF1F","link":"#\u5982\u4F55\u7CFB\u7EDF\u5B66\u4E60-typescript\uFF1F","children":[]},{"level":2,"title":"\u8FD9\u95E8\u8BFE\u7A0B\u662F\u5982\u4F55\u8BBE\u8BA1\u7684\uFF1F","slug":"\u8FD9\u95E8\u8BFE\u7A0B\u662F\u5982\u4F55\u8BBE\u8BA1\u7684\uFF1F","link":"#\u8FD9\u95E8\u8BFE\u7A0B\u662F\u5982\u4F55\u8BBE\u8BA1\u7684\uFF1F","children":[]},{"level":2,"title":"\u5199\u5728\u6700\u540E","slug":"\u5199\u5728\u6700\u540E","link":"#\u5199\u5728\u6700\u540E","children":[]},{"level":2,"title":"\u8BFE\u7A0B\u4E2D\u7684\u4EE3\u7801","slug":"\u8BFE\u7A0B\u4E2D\u7684\u4EE3\u7801","link":"#\u8BFE\u7A0B\u4E2D\u7684\u4EE3\u7801","children":[]}],"relativePath":"tsguide/1.\u5F00\u7BC7\uFF1A\u7528\u6B63\u786E\u7684\u65B9\u5F0F\u5B66\u4E60 TypeScript.md"}'),i={name:"tsguide/1.\u5F00\u7BC7\uFF1A\u7528\u6B63\u786E\u7684\u65B9\u5F0F\u5B66\u4E60 TypeScript.md"},c=r('<p>\u6700\u8FD1\uFF0C\u8F6F\u4EF6\u5F00\u53D1\u8BBE\u8BA1\u516C\u53F8 The Software House \u9488\u5BF9 2022 \u5E74\u524D\u7AEF\u5E02\u573A\u72B6\u6001\u7684\u8C03\u67E5\u663E\u793A\uFF0C84% \u7684\u53D7\u8BBF\u8005\u90FD\u5728\u4F7F\u7528 TypeScript\uFF0C43% \u7684\u53D7\u8BBF\u8005\u751A\u81F3\u8BA4\u4E3A TypeScript \u5C06\u8D85\u8D8A JavaScript \u6210\u4E3A\u524D\u7AEF\u5F00\u53D1\u7684\u4E3B\u8981\u8BED\u8A00\u3002</p><p>\u65E0\u8BBA TypeScript \u662F\u5426\u4F1A\u8D85\u8D8A JavaScript\uFF0C\u53EF\u4EE5\u80AF\u5B9A\u7684\u662F\uFF0CTypeScript \u5DF2\u88AB\u5F00\u53D1\u4EBA\u5458\u666E\u904D\u63A5\u53D7\uFF0C\u548C ES6 \u8BED\u6CD5\u4EE5\u53CA\u524D\u7AEF\u6846\u67B6\u4E00\u8D77\uFF0C\u88AB\u89C6\u4E3A\u524D\u7AEF\u5F00\u53D1\u9886\u57DF\u7684\u57FA\u7840\u5DE5\u5177\u3002\u4F46\u662F\uFF0C\u5173\u4E8E TypeScript \u7684\u8D28\u7591\u5374\u4E00\u76F4\u6CA1\u6709\u51CF\u5C11\uFF0C\u6BD4\u5982\uFF1A</p><ul><li>TypeScript \u9650\u5236\u4E86 JavaScript \u7684\u7075\u6D3B\u6027\uFF1B</li><li>TypeScript \u5E76\u4E0D\u80FD\u63D0\u9AD8\u5E94\u7528\u7A0B\u5E8F\u7684\u6027\u80FD\uFF1B</li><li>TypeScript \u5F00\u53D1\u9700\u8981\u66F4\u591A\u989D\u5916\u7684\u7C7B\u578B\u4EE3\u7801\u3002</li></ul><p>\u8FD9\u4E9B\u8D28\u7591\u5176\u5B9E\u53EF\u4EE5\u5F52\u7EB3\u4E3A\u4E00\u70B9\uFF1A<strong>TypeScript \u4F1A\u5F71\u54CD\u5B9E\u9645\u9879\u76EE\u7684\u5F00\u53D1\u6548\u7387</strong>\u3002\u4E8B\u5B9E\u771F\u7684\u662F\u8FD9\u6837\u5417\uFF1F</p><h2 id="\u4E3A\u4EC0\u4E48\u8BF4-typescript-\u53EF\u80FD\u8D85\u8D8A-javascript\uFF1F" tabindex="-1">\u4E3A\u4EC0\u4E48\u8BF4 TypeScript \u53EF\u80FD\u8D85\u8D8A JavaScript\uFF1F <a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u8BF4-typescript-\u53EF\u80FD\u8D85\u8D8A-javascript\uFF1F" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u77E5\u9053\uFF0CJavaScript \u4E00\u76F4\u4EE5\u7075\u6D3B\u6027\u8457\u79F0\u3002\u5728\u5B9E\u9645\u5F00\u53D1\u65F6\uFF0C\u6211\u4EEC\u4E0D\u9700\u8981\u786E\u5B9A\u4E00\u4E2A\u53D8\u91CF\u7684\u7C7B\u578B\uFF0C\u5C31\u80FD\u76F4\u63A5\u8BBF\u95EE\u53EF\u80FD\u5E76\u4E0D\u5B58\u5728\u7684\u5C5E\u6027\uFF0C\u6240\u4EE5\u65E0\u9700\u4E3A\u6BCF\u4E00\u6B65\u64CD\u4F5C\u90FD\u5B9A\u4E49\u7C7B\u578B\u3002\u5728\u5C0F\u578B\u9879\u76EE\u4E2D\uFF0C\u8FD9\u79CD\u7075\u6D3B\u6027\u53EF\u4EE5\u6709\u6548\u63D0\u9AD8\u5F00\u53D1\u6548\u7387\uFF0C\u5E2E\u52A9\u6211\u4EEC\u638C\u63A7\u5168\u5C40\u3002</p><p>\u4F46\u968F\u7740\u9879\u76EE\u89C4\u6A21\u7684\u589E\u5927\uFF0C\u8FD9\u4E9B\u53D8\u91CF\u7C7B\u578B\u7684\u6570\u91CF\u4E5F\u4F1A\u6210\u500D\u589E\u52A0\uFF0C\u4F60\u603B\u6709\u8BB0\u9519\u3001\u9057\u6F0F\u7684\u65F6\u5019\u3002\u6B64\u65F6\uFF0C\u7075\u6D3B\u6027\u5C31\u53D8\u6210\u4E86\u57CB\u5728\u9879\u76EE\u5185\u7684\u5B9A\u65F6\u70B8\u5F39\u3002\u5728\u300A<a href="https://link.juejin.cn/?target=https%3A%2F%2Frollbar.com%2Fblog%2Ftop-10-javascript-errors%2F" target="_blank" rel="noreferrer">Top 10 JavaScript errors from 1000+ projects</a>\u300B \u4E2D\uFF0C\u6700\u5E38\u89C1\u7684\u9519\u8BEF\u5C31\u662F <code>Cannot read property &#39;xxx&#39; of undefined</code>\u3001<code>undefined is not a function</code> \u7B49\u3002\u8FD9\u4E9B\u9519\u8BEF\u5C31\u662F\u6211\u4EEC\u8FC7\u5EA6\u4F9D\u8D56\u7075\u6D3B\u6027\u7684\u540E\u679C\uFF0C\u4E00\u65E6\u5B83\u4EEC\u88AB\u89E6\u53D1\uFF0C\u5C31\u4F1A\u5BFC\u81F4\u4F60\u7684\u9875\u9762\u767D\u5C4F\u3001\u5361\u6B7B\uFF0C\u751A\u81F3\u5D29\u6E83\u3002</p><p><strong>\u8981\u89E3\u51B3\u7075\u6D3B\u6027\u5E26\u6765\u7684\u9690\u60A3\uFF0C\u6211\u4EEC\u9700\u8981\u7684\u662F\u7C7B\u578B\u3002\u66F4\u51C6\u786E\u5730\u8BF4\uFF0C\u662F\u9879\u76EE\u5F00\u53D1\u65F6\u7684\u7C7B\u578B\u68C0\u67E5\u80FD\u529B\u3002</strong> TypeScript \u901A\u8FC7\u6613\u4E0A\u624B\u4E14\u529F\u80FD\u5F3A\u5927\u7684\u7C7B\u578B\u7CFB\u7EDF\uFF0C\u4E3A JavaScript \u63D0\u4F9B\u4E86\u5F3A\u5927\u7684\u7C7B\u578B\u68C0\u67E5\u80FD\u529B\u3002\u5728\u7C7B\u578B\u7684\u5E2E\u52A9\u4E0B\uFF0C\u6211\u4EEC\u65E0\u9700\u5B9E\u9645\u8FD0\u884C\u4EE3\u7801\uFF0C\u5C31\u80FD\u901A\u8FC7\u7C7B\u578B\u7684\u6D41\u8F6C\u89C2\u5BDF\u5230\u53D8\u91CF\u7684\u503C\u662F\u5982\u4F55\u6539\u53D8\u7684\u3002\u540C\u65F6\uFF0C\u7C7B\u578B\u7684\u6807\u8BB0\u4E5F\u80FD\u5E2E\u52A9\u6211\u4EEC\u786E\u4FDD\u6BCF\u4E00\u5904\u8BBF\u95EE\u3001\u8D4B\u503C\u4E0E\u64CD\u4F5C\u7684\u7C7B\u578B\u662F\u7B26\u5408\u9884\u671F\u7684\uFF0C\u6709\u6548\u51CF\u5C11\u6211\u4EEC\u9700\u8981\u627F\u53D7\u7684\u5FC3\u667A\u8D1F\u62C5\u3002</p><p>\u7531\u4E8E\u7C7B\u578B\u7684\u5F15\u5165\uFF0C<strong>TypeScript \u7684\u786E\u9650\u5236\u4E86 JavaScript \u7684\u7075\u6D3B\u6027\uFF0C\u4F46\u4E5F\u589E\u5F3A\u4E86\u9879\u76EE\u4EE3\u7801\u7684\u5065\u58EE\u6027</strong>\uFF0C\u5E76\u4E14\u5BF9\u4E8E\u5176\u4ED6\u540C\u5C5E\u4E8E\u7075\u6D3B\u6027\u7684\u4EE3\u8868\u7279\u6027\uFF0C\u5982 this\u3001\u539F\u578B\u94FE\u3001\u95ED\u5305\u4EE5\u53CA\u51FD\u6570\u7B49\uFF0CTypeScript \u4E1D\u6BEB\u6CA1\u6709\u9650\u5236\u3002</p><p>\u4F46\u662F\uFF0C\u60F3\u8981\u83B7\u5F97\u7C7B\u578B\u7684\u5E2E\u52A9\uFF0C\u6211\u4EEC\u9700\u8981\u989D\u5916\u7684\u7C7B\u578B\u4EE3\u7801\u3002\u8FD9\u4F1A\u5F71\u54CD\u5F00\u53D1\u6548\u7387\u5417\uFF1F\u4F1A\u6709\u5F71\u54CD\uFF0C\u4F46\u66F4\u591A\u662F\u6B63\u5411\u7684\u3002</p><p>\u9996\u5148\uFF0C\u8981\u5B9E\u73B0\u7C7B\u578B\u7684\u7CBE\u786E\u6807\u6CE8\uFF0C\u6211\u4EEC\u5E76\u4E0D\u4E00\u5B9A\u9700\u8981\u5927\u91CF\u7684\u7C7B\u578B\u4EE3\u7801\u3002\u5C31\u50CF\u4F60\u7ED9\u53D8\u91CF A \u65B0\u589E\u4E00\u4E9B\u5C5E\u6027\u5F97\u5230\u53D8\u91CF B\uFF0C\u4F60\u4F1A\u5B8C\u5168\u91CD\u65B0\u58F0\u660E\u8FD9\u4E24\u4E2A\u53D8\u91CF\u7684\u503C\u5417\uFF1F\u4E0D\u4F1A\u3002\u5728\u7C7B\u578B\u5C42\u9762\u4E5F\u662F\u4E00\u6837\uFF0C\u53D8\u91CF B \u7684\u7C7B\u578B\u4F1A\u57FA\u4E8E\u53D8\u91CF A \u7684\u7C7B\u578B\u6D3E\u751F\u5F97\u5230\uFF0C\u5B83\u4EEC\u5E76\u4E0D\u662F\u4E24\u4E2A\u5B8C\u5168\u72EC\u7ACB\u7684\u7C7B\u578B\u3002\u57FA\u4E8E\u8FD9\u4E00\u65B9\u5F0F\uFF0C\u6211\u4EEC\u5C31\u80FD\u8BA9\u7C7B\u578B\u4E4B\u95F4\u5B58\u5728\u7D27\u5BC6\u7684\u5173\u8054\uFF0C\u51CF\u5C11\u5197\u4F59\u7684\u7C7B\u578B\u4EE3\u7801\u3002</p><p>\u5176\u6B21\uFF0C\u5F53\u9879\u76EE\u89C4\u6A21\u589E\u5927\u5230\u67D0\u4E00\u9608\u503C\u540E\uFF0C\u8FD9\u4E9B\u7C7B\u578B\u4EE3\u7801\u4F1A\u662F\u4F60\u5F00\u53D1\u65F6\u6700\u5F97\u529B\u7684\u52A9\u624B\uFF0C\u8BA9\u4F60\u6709\u5E95\u6C14\u5927\u80C6\u5730\u8FDB\u884C\u5404\u79CD\u903B\u8F91\u64CD\u4F5C\uFF0C\u4E0D\u9700\u8981\u5148\u628A\u8FD9\u4E9B\u503C\u90FD\u6253\u5370\u51FA\u6765\u786E\u8BA4\u4E00\u904D\u3002\u540C\u65F6\uFF0C\u7531\u4E8E TypeScript \u5F3A\u5927\u7684\u7C7B\u578B\u63A8\u5BFC\u80FD\u529B\uFF0C\u968F\u7740\u4F60\u5BF9\u53D8\u91CF\u8FDB\u884C\u5404\u79CD\u64CD\u4F5C\uFF0CTypeScript \u5C31\u4F1A\u81EA\u52A8\u5730\u63A8\u5BFC\u51FA\u53D8\u91CF\u6700\u7EC8\u7684\u7C7B\u578B\u3002\u4F60\u53EA\u9700\u8981\u786E\u4FDD\u7C7B\u578B\u7B26\u5408\uFF0C\u6700\u7EC8\u7684\u7ED3\u679C\u5C31\u662F\u7B26\u5408\u4F60\u9884\u671F\u7684\u3002\u800C\u5728\u4F60\u6572\u51FB\u4E0B <code>.</code> \u6765\u8BBF\u95EE\u4E00\u4E2A\u53D8\u91CF\u7684\u5C5E\u6027\u65F6\uFF0CTypeScript \u4E5F\u4F1A\u5C06\u6240\u6709\u7684\u5C5E\u6027\u5C55\u793A\u51FA\u6765\u4F9B\u4F60\u6311\u9009\u3002\u8FD9\u5C31\u662F\u7C7B\u578B\u4EE3\u7801\u5BF9\u5F00\u53D1\u6548\u7387\u7684\u63D0\u5347\u6240\u5728\u3002</p><p>\u800C\u5728\u6700\u7EC8\u7F16\u8BD1\u65F6\uFF0CTypeScript \u53C8\u4F1A\u5C06\u8FD9\u4E9B\u7C7B\u578B\u4EE3\u7801\u62B9\u9664\uFF0C\u8FD8\u7ED9\u4F60\u53EF\u4EE5\u76F4\u63A5\u653E\u8FDB\u6D4F\u89C8\u5668\u91CC\u8DD1\u7684\u3001\u7EAF\u7CB9\u7684 JavaScript \u4EE3\u7801\u3002\u56E0\u6B64\uFF0CTypeScript \u786E\u5B9E\u4E0D\u80FD\u63D0\u9AD8\u5E94\u7528\u7A0B\u5E8F\u7684\u6027\u80FD\uFF0C\u56E0\u4E3A\u6700\u7EC8\u8FD0\u884C\u7684\u4ECD\u7136\u662F JavaScript\u3002</p><p>\u603B\u7684\u6765\u8BF4\uFF0CTypeScript \u5BF9\u5F00\u53D1\u6548\u7387\u7684\u5F71\u54CD\u548C\u9879\u76EE\u7684\u89C4\u6A21\u606F\u606F\u76F8\u5173\u3002\u5728\u5C0F\u9879\u76EE\u4E2D\uFF0CTypeScript \u786E\u5B9E\u4E0D\u53EF\u907F\u514D\u5730\u964D\u4F4E\u4E86\u9879\u76EE\u7684\u5F00\u53D1\u6548\u7387\u3002\u4F46\u5982\u679C\u6211\u4EEC\u653E\u773C\u4E8E\u9879\u76EE\u7684\u6574\u4E2A\u751F\u547D\u5468\u671F\uFF0C\u5F97\u76CA\u4E8E\u4E25\u5BC6\u7684\u7C7B\u578B\u68C0\u67E5\u4E0E\u5982\u81C2\u4F7F\u6307\u7684\u7C7B\u578B\u63A8\u5BFC\uFF0CTypeScript \u4E0D\u4EC5\u907F\u514D\u4E86 JavaScript \u7075\u6D3B\u6027\u53EF\u80FD\u4F1A\u5E26\u6765\u7684\u9690\u60A3\uFF0C\u8FD8\u80FD\u8BA9\u4F60\u5728\u9762\u5BF9 Bug \u65F6\u66F4\u5FEB\u5730\u5B9A\u4F4D\u95EE\u9898\uFF0C\u8BA9\u7A0B\u5E8F\u8DD1\u5F97\u66F4\u7A33\u5B9A\u4E00\u4E9B\uFF01\u4ECE\u8FD9\u4E2A\u65B9\u9762\u6765\u8BF4\uFF0CTypeScript \u5BF9\u5F00\u53D1\u6548\u7387\u7684\u63D0\u5347\u662F\u7EC8\u8EAB\u5236\u7684\u3002</p><h2 id="\u5982\u4F55\u7CFB\u7EDF\u5B66\u4E60-typescript\uFF1F" tabindex="-1">\u5982\u4F55\u7CFB\u7EDF\u5B66\u4E60 TypeScript\uFF1F <a class="header-anchor" href="#\u5982\u4F55\u7CFB\u7EDF\u5B66\u4E60-typescript\uFF1F" aria-hidden="true">#</a></h2><p>\u968F\u7740\u8D8A\u6765\u8D8A\u591A\u7684\u524D\u7AEF\u5F00\u53D1\u8005\u5F00\u59CB\u5C1D\u8BD5\u5B66\u4E60\u548C\u4F7F\u7528 TypeScript\uFF0C\u7406\u8BBA\u4E0A TypeScript \u5E94\u8BE5\u5DF2\u7ECF\u5B8C\u5168\u53D6\u4EE3 JavaScript \u4E86\uFF0C\u4F46\u5B9E\u9645\u4E0A\u5E76\u6CA1\u6709\u3002\u4E00\u65B9\u9762\uFF0C\u5C31\u50CF\u6211\u4EEC\u524D\u9762\u6240\u8BF4\uFF0C\u9879\u76EE\u89C4\u6A21\u8F83\u5C0F\u6216\u90E8\u5206\u5F00\u53D1\u8005\u4ECD\u7136\u66F4\u504F\u7231\u7075\u6D3B\u7684 JavaScript \u3002\u53E6\u4E00\u65B9\u9762\uFF0C\u4F5C\u4E3A JavaScript \u4E2D\u4E0D\u5B58\u5728\u7684\u6982\u5FF5\uFF0C\u7C7B\u578B\u80FD\u529B\u76F8\u5173\u7684\u5B66\u4E60\u6210\u672C\u8BA9\u5F88\u591A\u5F00\u53D1\u8005\u505C\u7559\u5728\u5165\u95E8\u9636\u6BB5\uFF0C\u65E0\u6CD5\u524D\u8FDB\u3002</p><p>\u8BB8\u591A\u65F6\u5019\uFF0C<strong>\u9AD8\u6602\u7684\u5B66\u4E60\u6210\u672C\u5F80\u5F80\u6765\u81EA\u4E8E\u6211\u4EEC\u5BF9 TypeScript \u4E0D\u6B63\u786E\u7684\u8BA4\u77E5\uFF0C\u4EE5\u53CA\u9519\u8BEF\u7684\u5B66\u4E60\u8DEF\u5F84</strong>\u3002\u6BD4\u5982\u8BF4\uFF0C\u6709\u4EBA\u8BA4\u4E3A\u201C\u628A\u7C7B\u578B\u76F8\u5173\u7684\u6982\u5FF5\u5B66\u4E60\u5B8C\u5C31\u7B97\u638C\u63E1 TypeScript \u4E86\u201D\uFF0C\u6709\u4EBA\u8FC7\u4E86\u4E00\u904D\u6587\u6863\u548C\u793E\u533A\u6587\u7AE0\uFF0C\u7B80\u5355\u5730\u4E0A\u624B\u4F7F\u7528\u4E00\u4E0B\uFF0C\u5C31\u89C9\u5F97\u81EA\u5DF1\u7684 TypeScript \u6C34\u5E73\u5DF2\u7ECF\u76F8\u5F53\u4E0D\u9519\u4E86\u3002</p><p>\u9996\u5148\uFF0C\u901A\u8FC7\u793E\u533A\u8D44\u6E90\u81EA\u5B66\u5E76\u6CA1\u6709\u4EC0\u4E48\u95EE\u9898\uFF0C\u4F46\u597D\u5185\u5BB9\u592A\u5C11\uFF0C\u627E\u8D77\u6765\u4E5F\u9700\u8981\u5F88\u591A\u65F6\u95F4\u3002\u5176\u6B21\uFF0C\u4EFB\u4F55\u77E5\u8BC6\u70B9\u90FD\u4E0D\u80FD\u5B64\u7ACB\u5730\u5B66\u4E60\u3002\u4E0D\u7136\u5728\u5B9E\u9645\u5F00\u53D1\u65F6\uFF0C\u9047\u5230\u4E00\u4E2A\u590D\u6742\u70B9\u7684\u7C7B\u578B\u7F16\u7A0B\u573A\u666F\uFF0C\u6211\u4EEC\u5C31\u675F\u624B\u65E0\u7B56\u4E86\u3002</p><p>\u90A3\u4E48\uFF0C\u6211\u4EEC\u5230\u5E95\u8BE5\u600E\u4E48\u5B66\u4E60 TypeScript \u5462\uFF1F<strong>\u5B66\u4E60\u6CA1\u6709\u6377\u5F84\uFF0C\u6700\u597D\u7684\u5B66\u4E60\u65B9\u6CD5\u5C31\u662F\u5148\u5EFA\u7ACB\u8D77\u5BF9\u4E8B\u7269\u7684\u5168\u9762\u8BA4\u77E5\uFF0C\u7136\u540E\u7531\u6D45\u5165\u6DF1\u5730\u7CFB\u7EDF\u5B66\u4E60\u3002</strong> \u8FD9\u91CC\u7684\u201C\u5168\u9762\u201D\u5982\u4F55\u7406\u89E3\uFF1F\u6211\u4EEC\u9996\u5148\u8981\u4E86\u89E3 TypeScript \u662F\u7531\u54EA\u4E9B\u90E8\u5206\u7EC4\u6210\u7684\u3002\u76F8\u5BF9\u4E25\u8C28\u6765\u8BF4\uFF0CTypeScript \u7531\u4E09\u4E2A\u90E8\u5206\u7EC4\u6210\uFF1A<strong>\u7C7B\u578B\u3001\u8BED\u6CD5\u4E0E\u5DE5\u7A0B</strong>\u3002\u6211\u4EEC\u53EF\u4EE5\u4ECE\u8FD9\u4E09\u4E2A\u90E8\u5206\u5165\u624B\uFF0C\u6765\u5EFA\u7ACB\u8D77\u4E00\u4E2A\u5168\u9762\u3001\u7CFB\u7EDF\u7684\u5B66\u4E60\u8DEF\u5F84\u3002</p><p>\u9996\u5148\u662F<strong>\u7C7B\u578B\u80FD\u529B</strong>\u3002\u5B83\u662F\u6700\u6838\u5FC3\u7684\u90E8\u5206\uFF0C\u4E5F\u662F\u5B66\u4E60\u6210\u672C\u6700\u9AD8\u7684\u90E8\u5206\u3002\u5B83\u4E3A JavaScript \u4E2D\u7684\u53D8\u91CF\u3001\u51FD\u6570\u7B49\u6982\u5FF5\u63D0\u4F9B\u4E86\u7C7B\u578B\u7684\u6807\u6CE8\uFF0C\u540C\u65F6\u5185\u7F6E\u4E86\u4E00\u6279\u7C7B\u578B\u5DE5\u5177\uFF0C\u57FA\u4E8E\u8FD9\u4E9B\u7C7B\u578B\u5DE5\u5177\u6211\u4EEC\u5C31\u80FD\u5B9E\u73B0\u66F4\u590D\u6742\u7684\u7C7B\u578B\u63CF\u8FF0\uFF0C\u5C06\u7C7B\u578B\u5173\u8054\u8D77\u6765\u3002\u4F60\u53EF\u4EE5\u4ECE\u6700\u7B80\u5355\u7684\u7C7B\u578B\u5F00\u59CB\uFF0C\u6BCF\u5B66\u5230\u4E00\u4E2A\u65B0\u7684\u7C7B\u578B\u80FD\u529B\u5C31\u6DFB\u52A0\u4E0A\u53BB\uFF0C\u601D\u8003\u5B83\u548C\u5DF2\u638C\u63E1\u90E8\u5206\u7684\u4EA4\u76F8\u878D\u5408\uFF0C\u4EE5\u6B64\u4E00\u6B65\u6B65\u638C\u63E1\u6574\u4E2A\u7C7B\u578B\u7CFB\u7EDF\u3002</p><p>\u63A5\u7740\u662F<strong>\u8BED\u6CD5\u90E8\u5206</strong>\u3002TypeScript \u63D0\u524D\u652F\u6301\u4E86\u4E00\u4E9B\u5DF2\u7ECF\u5230\u8FBE Stage 3 / 4 \u9636\u6BB5\uFF0C\u6216\u662F\u6BD4\u8F83\u91CD\u8981\u7684 TC39 \u63D0\u6848\uFF0C\u6BD4\u5982\u4F7F\u7528\u6700\u591A\u7684\u53EF\u9009\u94FE\uFF08<code>?.</code>\uFF09\u3001\u7A7A\u503C\u5408\u5E76\uFF08<code>??</code>\uFF09\u3001\u88C5\u9970\u5668\u7B49\uFF0C\u8FD9\u4E9B\u8BED\u6CD5\u90FD\u5DF2\u7ECF\u6216\u5373\u5C06\u6210\u4E3A ECMAScript Next \u7684\u65B0\u6210\u5458\u3002\u5728 TypeScript \u4E2D\u4F7F\u7528\u8FD9\u4E9B\u65B0\u8BED\u6CD5\u65F6\uFF0C\u4F60\u53EA\u9700\u8981\u7B80\u5355\u7684\u914D\u7F6E\u5C31\u80FD\u5B9E\u73B0\u8BED\u6CD5\u7684\u964D\u7EA7\uFF0C\u8BA9\u7F16\u8BD1\u540E\u7684\u4EE3\u7801\u53EF\u4EE5\u8FD0\u884C\u5728\u66F4\u4F4E\u7684\u6D4F\u89C8\u5668\u6216 Node \u7248\u672C\u4E0B\u3002\u8FD9\u4E00\u90E8\u5206\u51E0\u4E4E\u6CA1\u6709\u5B66\u4E60\u6210\u672C\uFF0C\u4ED6\u4EEC\u5C31\u50CF\u8BED\u8A00\u7684 API\uFF0C\u4F60\u53EA\u9700\u8981\u591A\u63D0\u9192\u81EA\u5DF1\u53BB\u4F7F\u7528\uFF0C\u53CA\u65F6\u67E5\u8BE2\u5B98\u65B9\u6587\u6863\u5C31\u80FD\u719F\u6089\u4E86\u3002</p><p>\u7C7B\u578B\u80FD\u529B\u4E0E\u65B0\u8BED\u6CD5\u786E\u5B9E\u5F88\u68D2\uFF0C\u4F46\u6D4F\u89C8\u5668\u4E0D\u8BA4\u600E\u4E48\u529E\uFF1FTypeScript \u4F1A\u5728\u6784\u5EFA\u65F6\u88AB\u62B9\u9664\u7C7B\u578B\u4EE3\u7801\u4E0E\u8BED\u6CD5\u7684\u964D\u7EA7\u3002\u8FD9\u4E00\u80FD\u529B\u5C31\u662F\u901A\u8FC7 TypeScript Compiler\uFF08tsc\uFF09\u5B9E\u73B0\u7684\u3002tsc \u4EE5\u53CA tsc \u914D\u7F6E\uFF08TSConfig\uFF09\u662F TypeScript \u5DE5\u7A0B\u5C42\u9762\u7684\u91CD\u8981\u90E8\u5206\u3002\u9664\u6B64\u4EE5\u5916\uFF0CTypeScript \u5DE5\u7A0B\u80FD\u529B\u7684\u53E6\u4E00\u91CD\u8981\u4F53\u73B0\u5C31\u662F\uFF0C\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u7C7B\u578B\u58F0\u660E\u7684\u65B9\u5F0F\uFF0C\u5728 TypeScript \u4E2D\u6109\u5FEB\u5730\u4F7F\u7528 JavaScript \u793E\u533A\u7684\u5927\u91CF npm \u5305\u3002</p><p>\u7C7B\u578B\u3001\u8BED\u6CD5\u3001\u5DE5\u7A0B\u5176\u5B9E\u4E5F\u4EE3\u8868\u4E86\u4E09\u4E2A\u4E0D\u540C\u9636\u6BB5\u4F7F\u7528 TypeScript \u76EE\u7684\uFF1A<strong>\u4E3A JavaScript \u4EE3\u7801\u6DFB\u52A0\u7C7B\u578B\u4E0E\u7C7B\u578B\u68C0\u67E5\u6765\u786E\u4FDD\u5065\u58EE\u6027\uFF0C\u63D0\u524D\u4F7F\u7528\u65B0\u8BED\u6CD5\u6216\u65B0\u7279\u6027\u6765\u7B80\u5316\u4EE3\u7801\uFF0C\u4EE5\u53CA\u6700\u7EC8\u83B7\u5F97\u53EF\u7528\u7684 JavaScript \u4EE3\u7801\u3002\u56E0\u6B64\uFF0C\u7C7B\u578B-\u8BED\u6CD5-\u5DE5\u7A0B\uFF0C\u4E5F\u662F\u5B66\u4E60 TypeScript \u7684\u6700\u4F73\u8DEF\u5F84\u3002</strong></p><h2 id="\u8FD9\u95E8\u8BFE\u7A0B\u662F\u5982\u4F55\u8BBE\u8BA1\u7684\uFF1F" tabindex="-1">\u8FD9\u95E8\u8BFE\u7A0B\u662F\u5982\u4F55\u8BBE\u8BA1\u7684\uFF1F <a class="header-anchor" href="#\u8FD9\u95E8\u8BFE\u7A0B\u662F\u5982\u4F55\u8BBE\u8BA1\u7684\uFF1F" aria-hidden="true">#</a></h2><p>\u6309\u7167\u6211\u4EEC\u4E0A\u9762\u8BB2\u5230\u7684\u5B66\u4E60\u8DEF\u5F84\uFF0C\u8FD9\u95E8\u8BFE\u7A0B\u4E5F\u5C06\u5206\u4E3A<strong>\u7C7B\u578B\u80FD\u529B\u7BC7\u3001\u8BED\u6CD5\u7BC7\u548C\u5DE5\u7A0B\u5B9E\u8DF5\u7BC7</strong>\u3002\u4ECE\u4E0B\u56FE\u4E2D\u4E5F\u80FD\u770B\u51FA\uFF0C\u6211\u4EEC\u4F1A\u5C06\u91CD\u70B9\u66F4\u591A\u5730\u653E\u5728\u7C7B\u578B\u90E8\u5206\u3002</p><p><img src="https://p3-juejin.byteimg.com/tos-cn-i-k3u1fbpfcp/214c20c9e0314b8ca2a6c9cecc45e7bf~tplv-k3u1fbpfcp-zoom-in-crop-mark:3024:0:0:0.awebp" alt="img"></p><p>\u6211\u4EEC\u4F1A\u4ECE TypeScript \u7684\u7C7B\u578B\u57FA\u7840\u5F00\u59CB\u5B66\u4E60\uFF0C\u7136\u540E\u5230\u6CDB\u578B\u3001\u6761\u4EF6\u7C7B\u578B\u7B49\u8FD9\u4E9B\u9996\u6B21\u89C1\u9762\u7684\u7C7B\u578B\u5DE5\u5177\u3002\u5728\u8FD9\u4E9B\u7C7B\u578B\u5DE5\u5177\u7684\u5B66\u4E60\u4E0E\u4F7F\u7528\u4E2D\uFF0C\u6211\u4EEC\u4F1A\u9047\u5230\u8BB8\u591A\u56F0\u60D1\u3002\u8FD9\u65F6\u4E0D\u59A8\u8F6C\u6362\u4E0B\u65B9\u5411\uFF0C\u6765\u4E86\u89E3 TypeScript \u7684\u7C7B\u578B\u7CFB\u7EDF\uFF0C\u5305\u62EC\u5B83\u662F\u5982\u4F55\u8FDB\u884C\u7C7B\u578B\u6BD4\u8F83\u7684\uFF0C\u4EE5\u53CA\u6574\u4E2A\u7C7B\u578B\u4E16\u754C\u7684\u8FD0\u884C\u89C4\u5219\u662F\u600E\u6837\u7684\u3002\u8FD9\u4E00\u6B65\u5C06\u5E2E\u52A9\u4F60\u4ECE\u6839\u6E90\u4E0A\u7406\u89E3 TypeScript \u7684\u7C7B\u578B\u80FD\u529B\u8BBE\u8BA1\u4E0E\u5E95\u5C42\u8FD0\u884C\u89C4\u5219\u3002</p><p>\u638C\u63E1\u4E86\u7C7B\u578B\u5DE5\u5177\u4E0E\u7C7B\u578B\u7CFB\u7EDF\u540E\uFF0C\u6211\u4EEC\u624D\u4F1A\u5B66\u4E60\u7C7B\u578B\u80FD\u529B\u6700\u590D\u6742\u7684\u6982\u5FF5\uFF1A\u7C7B\u578B\u7F16\u7A0B\u3002\u8FD9\u90E8\u5206\u6211\u4EEC\u4F1A\u4ECE\u5185\u7F6E\u5DE5\u5177\u7C7B\u578B\u8BB2\u89E3\u5230\u5185\u7F6E\u5DE5\u5177\u7C7B\u578B\u8FDB\u9636\uFF0C\u5E26\u4F60\u5FAA\u5E8F\u6E10\u8FDB\u5730\u638C\u63E1\u7C7B\u578B\u7F16\u7A0B\u4E2D\u7684 4 \u5927\u8303\u5F0F\u3002</p><p>\u901A\u8FC7\u8FD9\u4E00\u8DEF\u7EBF\uFF0C\u4F60\u5C06\u5EFA\u7ACB\u8D77\u5168\u9762\u7684 TypeScript \u7C7B\u578B\u80FD\u529B\u77E5\u8BC6\u4F53\u7CFB\uFF0C\u61C2\u5F97\u5982\u4F55\u7ED3\u5408\u4F7F\u7528\u5404\u79CD\u7C7B\u578B\u5DE5\u5177\u6765\u8FDB\u884C\u7C7B\u578B\u7F16\u7A0B\uFF0C\u5982\u4F55\u72EC\u7ACB\u89E3\u51B3\u5404\u79CD\u7C7B\u578B\u62A5\u9519\uFF0C\u4EE5\u53CA\u6700\u91CD\u8981\u7684\u80FD\u529B\u2014\u2014\u7406\u89E3\u7C7B\u578B\u4E16\u754C\u7684\u57FA\u672C\u89C4\u5219\u4E0E\u8FD0\u884C\u89C4\u5F8B\u3002</p><p>\u8FC8\u8FC7\u4E86\u7C7B\u578B\u8FD9\u5EA7\u5927\u5C71\u4EE5\u540E\uFF0C\u6211\u4EEC\u5C06\u9762\u5BF9\u65B0\u670B\u53CB\uFF1A\u8BED\u6CD5\uFF0C\u5B83\u4EEC\u6BD4\u7C7B\u578B\u90E8\u5206\u8F7B\u677E\u53CB\u597D\u591A\u4E86\u3002\u6211\u4EEC\u4F1A\u4ECE TypeScript \u4E0E ECMAScript \u7684\u5173\u7CFB\u5F00\u59CB\u8BF4\u8D77\uFF0C\u4ECB\u7ECD\u8FD9\u4E9B\u65B0\u8BED\u6CD5\u7684\u4F7F\u7528\uFF0C\u5BF9\u5176\u4E2D\u7684\u91CD\u91CF\u7EA7\u89D2\u8272\u88C5\u9970\u5668\uFF0C\u6211\u4EEC\u66F4\u4F1A\u642D\u914D\u5B9E\u6218\u3002</p><p>\u5728\u6700\u540E\u7684\u5DE5\u7A0B\u90E8\u5206\uFF0C\u9664\u4E86\u6211\u4EEC\u5DF2\u7ECF\u63D0\u5230\u7684 tsc \u4EE5\u53CA TSConfig \uFF0C\u6211\u4EEC\u8FD8\u4F1A\u6709\u66F4\u591A\u7684\u5DE5\u7A0B\u573A\u666F\u5B9E\u6218\uFF0C\u7C7B\u578B\u58F0\u660E\u4E0E\u7C7B\u578B\u6307\u4EE4\uFF0C\u4E0E React / ESLint \u7B49\u6846\u67B6\u6216\u5DE5\u5177\u7684\u7D27\u5BC6\u7ED3\u5408\uFF0C\u5F00\u53D1\u5E76\u90E8\u7F72\u4E00\u4E2A\u57FA\u4E8E TypeScript \u7684 Node API \u3002\u5C31\u50CF\u8FD9\u672C\u5C0F\u518C\u7684\u540D\u5B57\u300A TypeScript \u5168\u9762\u8FDB\u9636\u6307\u5357 \u300B\u4E00\u6837\uFF0C\u6211\u4EEC\u7684\u76EE\u6807\u662F\u5168\u65B9\u4F4D\u3001\u65E0\u6B7B\u89D2\u5730\u638C\u63E1 TypeScript \u3002</p><h2 id="\u5199\u5728\u6700\u540E" tabindex="-1">\u5199\u5728\u6700\u540E <a class="header-anchor" href="#\u5199\u5728\u6700\u540E" aria-hidden="true">#</a></h2><p>\u65E0\u8BBA\u4F60\u5904\u4E8E\u54EA\u4E2A\u9636\u6BB5\uFF0C\u53EA\u8981\u60F3\u5F00\u59CB\u5B66\u4E60 TypeScript\uFF0C\u8FD9\u95E8\u8BFE\u7A0B\u5C31\u662F\u9002\u5408\u4F60\u7684\u3002\u53EA\u8981\u4F60\u6CBF\u7740\u8BFE\u7A0B\u4E2D\u7ED9\u51FA\u7684\u8DEF\u7EBF\uFF0C\u5B8C\u6210\u8FD9\u4E00\u8DEF\u4E0A\u7684\u5404\u79CD\u4EFB\u52A1\uFF0C\u5C31\u4E00\u5B9A\u80FD\u4ECE\u65B0\u624B\u6210\u529F\u8FC8\u5411\u9AD8\u7EA7\u73A9\u5BB6\u3002</p><p>\u540C\u65F6\u6211\u4E5F\u5E0C\u671B\uFF0C\u4F60\u53EF\u4EE5\u628A\u5B83\u5F53\u4F5C\u4E00\u672C\u5907\u5FD8\u624B\u518C\uFF0C\u65F6\u5E38\u56DE\u987E\u548C\u7FFB\u9605\u3002\u6BD4\u5982\u8BF4\uFF0C\u5F53\u4F60\u5FD8\u8BB0\u4E86 never \u7C7B\u578B\u7684\u5177\u4F53\u610F\u4E49\uFF0C\u5F53\u4F60\u5FD8\u8BB0\u4E86 tsconfig \u7684\u67D0\u4E00\u4E2A\u914D\u7F6E\u5177\u4F53\u4F5C\u7528\uFF0C\u5F53\u4F60\u5FD8\u8BB0\u4E86\u67D0\u4E00\u7C7B\u5DE5\u5177\u7C7B\u578B\u7684\u5B9E\u73B0\u539F\u7406\uFF0C\u4F60\u90FD\u53EF\u4EE5\u56DE\u6765\u5728\u5C0F\u518C\u4E2D\u627E\u5230\u7B54\u6848\u3002</p><p>\u6700\u540E\uFF0C\u4E5F\u6B22\u8FCE\u4F60\u5728\u4EA4\u6D41\u7FA4\u6216 GitHub \u4ED3\u5E93\u7684 issue \u533A\uFF0C\u4E0E\u6211\u4EA4\u6D41 TypeScript \u5B66\u4E60\u8DEF\u4E0A\u7684\u95EE\u9898\u3002\u9664\u4E86\u4E0E\u5176\u4ED6\u8BFB\u8005\u4E92\u76F8\u4EA4\u6D41\u89E3\u60D1\u4EE5\u5916\uFF0C\u6211\u4E5F\u4F1A\u5B89\u6392\u56FA\u5B9A\u7684\u7B54\u7591\u65F6\u95F4\u6765\u7EDF\u4E00\u89E3\u51B3\u95EE\u9898\uFF0C\u671F\u5F85\u4E0E\u4F60\u4E00\u540C\u8FDB\u6B65\u3002</p><blockquote><h2 id="\u8BFE\u7A0B\u4E2D\u7684\u4EE3\u7801" tabindex="-1">\u8BFE\u7A0B\u4E2D\u7684\u4EE3\u7801 <a class="header-anchor" href="#\u8BFE\u7A0B\u4E2D\u7684\u4EE3\u7801" aria-hidden="true">#</a></h2><p>\u5C0F\u518C\u4E2D\u7684\u6240\u6709\u4EE3\u7801\u90FD\u4F1A\u5B58\u653E\u5728 <strong><a href="https://link.juejin.cn/?target=https%3A%2F%2Fgithub.com%2Flinbudu599%2FTypeScript-Tiny-Book" target="_blank" rel="noreferrer">TypeScript-Tiny-Book</a></strong> \u8FD9\u4E2A\u4ED3\u5E93\u91CC\uFF0C\u57FA\u4E8E Monorepo \u67B6\u6784\u6765\u9694\u79BB\u4E86\u5404\u4E2A\u4E0D\u540C\u90E8\u5206\u7684\u793A\u4F8B\u4EE3\u7801\uFF0C\u540C\u65F6\u5728\u6BCF\u4E00\u8282\u4E5F\u4F1A\u7ED9\u51FA\u5BF9\u5E94\u4EE3\u7801\u7684\u5730\u5740\u3002</p></blockquote>',36),a=[c];function o(n,s,S,T,y,l){return e(),t("div",null,a)}const g=p(i,[["render",o]]);export{h as __pageData,g as default};
