import{_ as e,c as p,o as t,a as r}from"./app.c54055ba.js";const T=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4ECE\u7C7B\u578B\u7F16\u7A0B\u5230\u7C7B\u578B\u4F53\u64CD","slug":"\u4ECE\u7C7B\u578B\u7F16\u7A0B\u5230\u7C7B\u578B\u4F53\u64CD","link":"#\u4ECE\u7C7B\u578B\u7F16\u7A0B\u5230\u7C7B\u578B\u4F53\u64CD","children":[]},{"level":2,"title":"Type Challenges","slug":"type-challenges","link":"#type-challenges","children":[]}],"relativePath":"tsguide/16.\u4E86\u89E3\u7C7B\u578B\u7F16\u7A0B\u4E0E\u7C7B\u578B\u4F53\u64CD\u7684\u610F\u4E49\uFF0C\u627E\u5230\u5E73\u8861\u70B9.md"}'),a={name:"tsguide/16.\u4E86\u89E3\u7C7B\u578B\u7F16\u7A0B\u4E0E\u7C7B\u578B\u4F53\u64CD\u7684\u610F\u4E49\uFF0C\u627E\u5230\u5E73\u8861\u70B9.md"},i=r('<p>\u5728\u5B66\u4E60 TypeScript \u65F6\uFF0C\u5F88\u591A\u540C\u5B66\u53EF\u80FD\u4F1A\u9047\u5230\u8FD9\u4E9B\u7591\u60D1\uFF1A</p><ul><li>\u8FD9\u4E9B\u989D\u5916\u7684\u7C7B\u578B\u4EE3\u7801\uFF0C\u4EE5\u53CA\u7C7B\u578B\u7F16\u7A0B\uFF0C\u5BF9\u5B9E\u9645\u9879\u76EE\u5F00\u53D1\u7684\u5E2E\u52A9\u5728\u54EA\u91CC\uFF1F\u6211\u771F\u7684\u9700\u8981\u7CBE\u901A\u5B83\u4EEC\u5417\uFF1F</li><li>\u8FD9\u4E9B\u5DE5\u5177\u7C7B\u578B\u770B\u8D77\u6765\u771F\u7684\u597D\u70E7\u8111\uFF0C\u5E76\u4E14\u5B83\u4EEC\u5E76\u4E0D\u80FD\u8BA9\u6211\u7684\u7F51\u9875\u8FD0\u884C\u5F97\u66F4\u5FEB\uFF0C\u8BA9\u6211\u7684 Node \u670D\u52A1\u5E76\u53D1\u66F4\u9AD8\uFF0C\u8BA9\u6211\u7684\u7F51\u9875 PV / UV \u6570\u636E\u66F4\u559C\u4EBA\uFF0C\u6211\u4E3A\u4EC0\u4E48\u9700\u8981\u4E86\u89E3\u5B83\u4EEC\uFF1F</li><li>TypeScript \u7684\u5B66\u4E60\u66F2\u7EBF\u662F\u600E\u4E48\u6837\u7684\uFF1F\u662F\u5426\u5B58\u5728\u4E00\u4E2A\u6BD4\u8F83\u5E73\u8861\u7684\u9636\u6BB5\uFF0C\u8BA9\u6211\u7684\u6295\u5165\u4EA7\u51FA\u6BD4\u6700\u5927\u5316\uFF1F</li></ul><p>\u6240\u4EE5\u8FD9\u4E00\u8282\uFF0C\u6211\u4EEC\u4E0D\u5B66\u4E60\u4EE3\u7801\uFF0C\u53EA\u662F\u6765\u804A\u4E00\u804A TypeScript \u7C7B\u578B\u7F16\u7A0B\u7684\u5B66\u4E60\u610F\u4E49\u3002\u56E0\u4E3A\u6B64\u65F6\u4F60\u5DF2\u7ECF\u5177\u5907\u4E86\u6DF1\u5165\u5B66\u4E60\u7C7B\u578B\u7F16\u7A0B\u7684\u524D\u7F6E\u57FA\u7840\uFF0C\u53C8\u9A6C\u4E0A\u8981\u8FDB\u5165\u5DE5\u5177\u7C7B\u578B\u8FDB\u9636\u90E8\u5206\u7684\u5B66\u4E60\u3002\u90A3\u5728\u6311\u6218\u8FD9\u5EA7\u6700\u9AD8\u7684\u201C\u5C71\u5CF0\u201D\u4E4B\u524D\uFF0C\u6211\u4EEC\u5F88\u6709\u5FC5\u8981\u5229\u7528\u8FD9\u51E0\u4E2A\u95EE\u9898\u5E26\u4F60\u8D70\u4E00\u4E9B\u5E38\u89C1\u7684\u8BEF\u533A\uFF0C\u8BA9\u4F60\u80FD\u5FC3\u65E0\u65C1\u9A9B\u5730\u575A\u5B9A\u5411\u524D\u3002</p><h2 id="\u4ECE\u7C7B\u578B\u7F16\u7A0B\u5230\u7C7B\u578B\u4F53\u64CD" tabindex="-1">\u4ECE\u7C7B\u578B\u7F16\u7A0B\u5230\u7C7B\u578B\u4F53\u64CD <a class="header-anchor" href="#\u4ECE\u7C7B\u578B\u7F16\u7A0B\u5230\u7C7B\u578B\u4F53\u64CD" aria-hidden="true">#</a></h2><p>\u4F60\u53EF\u80FD\u4F1A\u6CE8\u610F\u5230\uFF0C\u4E0A\u9762\u6211\u7528\u4E86\u201C\u7C7B\u578B\u7F16\u7A0B\u201D\u548C\u201C\u7C7B\u578B\u4F53\u64CD\u201D\u8FD9\u4E24\u4E2A\u4E0D\u540C\u7684\u8BCD\u3002\u8FD9\u662F\u56E0\u4E3A\u6211\u901A\u5E38\u628A TS \u4E2D\u7684\u7C7B\u578B\u64CD\u4F5C\u5206\u4E3A\u8FD9\u4E24\u7C7B\u3002\u5BF9\u4E8E\u7C7B\u578B\u7F16\u7A0B\uFF0C\u5B83\u662F<strong>\u5BF9\u5B9E\u9645\u5F00\u53D1\u4E2D\u771F\u7684\u6709\u5E2E\u52A9\u7684\u7C7B\u578B\u64CD\u4F5C</strong>\uFF0C\u4E0B\u9650\u975E\u5E38\u4E4B\u4F4E\uFF0C\u6BD4\u5982\u5176\u5B9E\u6211\u4EEC\u5C31\u662F\u7B80\u5355\u5730\u7528\u4E2A\u6CDB\u578B\uFF0C\u8FD9\u4E5F\u5C5E\u4E8E\u7C7B\u578B\u7F16\u7A0B\u3002\u800C\u5B83\u7684\u4E0A\u9650\u4E5F\u5F88\u9AD8\uFF0C\u6BD4\u5982\u5E95\u5C42\u6846\u67B6\u4E2D\u8BA9\u4EBA\u773C\u82B1\u7F2D\u4E71\u7684\u64CD\u4F5C\u3002\u4F46\u6211\u4EEC\u4E0D\u9700\u8981\u7528\u4E00\u4E2A\u5177\u4F53\u7684\u754C\u9650\u6765\u8FDB\u884C\u5212\u5206\uFF0C\u53EA\u9700\u8981\u77E5\u9053\uFF0C\u53EA\u8981\u662F\u771F\u7684\u5BF9\u5B9E\u9645\u5F00\u53D1\u6709\u5E2E\u52A9\u7684\u7C7B\u578B\u64CD\u4F5C\uFF0C\u65E0\u8BBA\u5B9E\u73B0\u591A\u4E48\u590D\u6742\uFF0C\u90FD\u80FD\u88AB\u5F52\u7C7B\u4E8E\u7C7B\u578B\u7F16\u7A0B\u5F53\u4E2D\u3002</p><p>\u90A3\u4E48\u7C7B\u578B\u4F53\u64CD\u53C8\u662F\u4EC0\u4E48\u6837\u7684\uFF1F\u5728\u6B64\u4E4B\u524D\uFF0C\u4F60\u53EF\u80FD\u770B\u5230\u8FC7\u57FA\u4E8E TypeScript \u7C7B\u578B\u5B9E\u73B0\u7684\u56DB\u5219\u8FDC\u7B97\u3001\u6590\u6CE2\u90A3\u5951\u6570\u5217\u3001\u8C61\u68CB\u3001Lisp \u7F16\u8BD1\u5668\u8FD9\u4E00\u7C7B\u4EE4\u4EBA\u53F9\u4E3A\u89C2\u6B62\u7684\u64CD\u4F5C\uFF0C\u8FD9\u4E9B\u5C31\u5C5E\u4E8E\u7C7B\u578B\u4F53\u64CD\u3002\u4F46\u8BF7\u6CE8\u610F\uFF0C\u8FD9\u4E2A\u8BCD\u5E76\u4E0D\u662F\u8D2C\u4E49\u7684\uFF0C\u76F8\u53CD\uFF0C\u5927\u90E8\u5206\u7C7B\u578B\u4F53\u64CD\u7684\u4F5C\u8005\u90FD\u662F\u7F16\u7A0B\u529F\u5E95\u5341\u5206\u6DF1\u539A\u7684\u5927\u725B\uFF0C\u4ED6\u4EEC\u53D1\u660E\u8FD9\u4E9B\u4F53\u64CD\u7684\u610F\u56FE\u5E76\u4E0D\u662F\u4E3A\u4E86\u70AB\u6280\uFF0C\u53EA\u662F\u4E3A\u4E86\u5C55\u793A\u7ED9\u840C\u65B0\u4EEC\u65B0\u4E16\u754C\u7684\u5927\u95E8\uFF0C\u6211\u4E2A\u4EBA\u4E5F\u975E\u5E38\u4F69\u670D\u80FD\u5199\u51FA\u5982\u6B64\u9AD8\u9636\u7C7B\u578B\u8FD0\u7B97\u7684\u5927\u4F6C\u4EEC\u3002\u8FD9\u4E9B\u4F53\u64CD\u867D\u7136\u5BF9\u5B9E\u9645\u9879\u76EE\u5F00\u53D1\u7684\u610F\u4E49\u975E\u5E38\u6709\u9650\uFF0C\u4F46\u5374\u53EF\u4EE5\u4ECE\u53E6\u5916\u4E00\u4E2A\u89D2\u5EA6\u8BA9\u4F60\u8BA4\u8BC6\u5230 TypeScript \u7C7B\u578B\u7CFB\u7EDF\u7684\u56FE\u7075\u5B8C\u5907\uFF0C\u4EE5\u53CA\u5728\u9AD8\u624B\u624B\u91CC\u6240\u8868\u73B0\u51FA\u6765\u7684\u7CBE\u5999\u6280\u5DE7\u3002</p><p>\u4F46\u662F\uFF0C\u5982\u679C\u4F60\u56E0\u4E3A\u770B\u4E0D\u61C2\u8FD9\u4E9B\u4F53\u64CD\uFF0C\u6216\u8005\u60F3\u4E0D\u51FA\u66F4\u70AB\u9177\u7684\u4F53\u64CD\u800C\u611F\u5230\u7126\u8651\uFF0C\u90A3\u5C31\u9700\u8981\u8B66\u9192\u4E00\u4E0B\u4E86\u2014\u2014<strong>\u7C7B\u578B\u4F53\u64CD\u7EDD\u4E0D\u4EE3\u8868\u4F60\u7684 TS \u6C34\u5E73</strong>\u3002\u6211\u770B\u5230\u8FC7\u5F88\u591A\u56E0\u4E3A\u638C\u63E1\u4E86\u5404\u79CD\u7C7B\u578B\u4F53\u64CD\u8868\u6F14\u5F15\u4EE5\u4E3A\u8C6A\u7684\u540C\u5B66\uFF0C\u5176\u4E2D\u6709\u76F8\u5F53\u4E00\u90E8\u5206\u4EBA\u5728\u5B9E\u9645\u4E1A\u52A1\u5F00\u53D1\u4E2D\uFF0C\u4ECD\u7136\u662F\u5404\u79CD any \u4E0E\u590D\u5236\u7C98\u8D34\u3002\u8FD9\u5C31\u662F\u975E\u5E38\u4E25\u91CD\u7684\u672C\u672B\u5012\u7F6E\uFF0C\u4F60\u5B66\u4E60 TypeScript \uFF0C\u662F\u4E3A\u4E86\u5E05\u6C14\u7684\u4F53\u64CD\u8FD8\u662F\u4E3A\u4E86\u66F4\u9AD8\u8D28\u91CF\u7684\u4EE3\u7801\u5462\uFF1F</p><p>\u5982\u679C\u4F60\u638C\u63E1\u4E86\u6240\u6709\u7684\u7C7B\u578B\u4F53\u64CD\uFF0C\u4F60\u5C31\u80FD\u5199\u597D\u6240\u6709\u7684\u4E1A\u52A1\u4EE3\u7801\u5417\uFF1F\u7B54\u6848\u663E\u7136\u662F\u5426\u5B9A\u7684\u3002\u7C7B\u578B\u4F53\u64CD\u5C31\u50CF\u662F\u4E94\u5E74\u9AD8\u8003\u4E09\u5E74\u6A21\u62DF\uFF0C\u53EA\u8981\u4F60\u613F\u610F\u82B1\u65F6\u95F4\uFF0C\u5BF9\u7740\u7B54\u6848\u603B\u662F\u80FD\u770B\u660E\u767D\u7684\uFF0C\u4F46\u4F60\u770B\u5B8C\u5C31\u80FD\u4FDD\u8BC1\u505A\u51FA\u6570\u5B66\u538B\u8F74\u9898\u7684\u6700\u540E\u4E00\u5C0F\u95EE\u5417\uFF1F\u7C7B\u578B\u4F53\u64CD\u7EDD\u4E0D\u5E94\u5F53\u88AB\u4F5C\u4E3A TS \u6C34\u5E73\u7684\u5EA6\u91CF\u8861\uFF0C\u5728\u7EDD\u5927\u591A\u6570\u60C5\u51B5\u4E0B\uFF0C\u6211\u4EEC\u5BF9\u4E8E TypeScript \u76F8\u5173\u7684\u6280\u5DE7\uFF0C\u90FD\u5E94\u8BE5\u79C9\u6301\u7740\u4ECE\u5B9E\u7528\u6027\u51FA\u53D1\u7684\u89D2\u5EA6\u6765\u770B\u5F85\u3002\u7C7B\u4F3C\u7684\uFF0C\u8FD8\u6709\u76EF\u7740 TypeScript \u6E90\u7801\u8BFB\uFF0C\u7C7B\u578B\u7F16\u7A0B\u8FD8\u6CA1\u5165\u95E8\u5C31\u53BB\u5237 Type Challenges \u7B49\u7B49\u8BEF\u533A\uFF0C\u5F53\u4F60\u53D1\u73B0\u5B66\u4E60\u8FDB\u5EA6\u6162\u5F97\u611F\u4EBA\uFF0C\u6389\u53D1\u8FDB\u5EA6\u5FEB\u5F97\u5413\u4EBA\uFF0C\u771F\u7684\u4E0D\u4F1A\u6000\u7591\u4EBA\u751F\u5417\uFF1F</p><p>\u6211\u4E2A\u4EBA\u66F4\u504F\u5411\u4E8E\u5B9E\u7528\u4E3B\u4E49\uFF0C\u5C31\u50CF\u8FD9\u672C\u5C0F\u518C\u4E00\u76F4\u5230\u73B0\u5728\uFF0C\u4EE5\u53CA\u63A5\u4E0B\u6765\u7684\u5185\u5BB9\u90FD\u662F\u63A5\u5730\u6C14\u7684\uFF0C\u4E0D\u5B58\u5728\u4F60\u5B66\u4E86\u5374\u65E0\u5904\u53EF\u7528\u7684\u77E5\u8BC6\u3002\u8FD9\u4E5F\u662F\u6211\u8BA4\u4E3A\u5728\u5B66\u4E60\u4E00\u95E8\u7F16\u7A0B\u8BED\u8A00/\u5DE5\u5177/\u6846\u67B6\u65F6\u6700\u5E94\u8BE5\u5148\u8003\u8651\u7684\u4E8B\u60C5\uFF0C<strong>\u4E00\u95E8\u6280\u672F\uFF0C\u8981\u4E48\u5E94\u5F53\u80FD\u591F\u5728\u5F53\u4E0B\u6216\u8005\u53EF\u9884\u89C1\u7684\u672A\u6765\u7ED9\u4E88\u4F60\u5E2E\u52A9\uFF0C\u8981\u4E48\u80FD\u591F\u6269\u5BBD\u4F60\u7684\u6280\u672F\u89C6\u91CE\u548C\u77E5\u8BC6\u8FB9\u754C\uFF08\u7C7B\u578B\u4F53\u64CD\u5E76\u4E0D\u5B8C\u5168\u7B97\uFF09</strong>\uFF0C\u800C\u7C7B\u578B\u4F53\u64CD\u5BF9\u4E8E\u65B0\u4EBA\u6765\u8BF4\u9664\u4E86\u5F92\u589E\u7126\u8651\u5E76\u6CA1\u6709\u4EFB\u4F55\u610F\u4E49\u3002</p><p>\u73B0\u5728\u6211\u4EEC\u53EF\u4EE5\u6765\u56DE\u7B54\u4E0B\u5F00\u59CB\u7684\u95EE\u9898\u4E86\u3002</p><p>TypeScript \u5E26\u6765\u4E86\u54EA\u4E9B\u5E2E\u52A9\uFF1F\u592A\u591A\u5566\uFF0C\u6211\u53EA\u8BB2\u4E00\u4E2A\u793E\u533A\u4E2D\u8FD8\u6CA1\u89C1\u8FC7\u6709\u4EBA\u63D0\u7684\u70B9\u3002\u6211\u4EEC\u4E66\u5199\u7684\u7C7B\u578B\u5B9E\u9645\u4E0A\u4E5F\u662F\u903B\u8F91\uFF0C\u53EA\u4E0D\u8FC7\u662F\u7C7B\u578B\u4E16\u754C\u7684\u903B\u8F91\uFF0C\u5E76\u4E14\u4E0D\u5B58\u5728\u4E8E\u8FD0\u884C\u65F6\u3002\u5728\u6211\u4EEC\u4E66\u5199\u5B9E\u9645\u7684\u4EE3\u7801\u903B\u8F91\u65F6\uFF0C\u8FD9\u4E9B\u7C7B\u578B\u903B\u8F91\u5C31\u50CF\u662F\u98DE\u673A\u8D77\u98DE\u524D\u7684\u9884\u68C0\uFF08preflight\uFF09\u4E00\u6837\uFF0C\u5728\u4F60\u7684\u4EE3\u7801\u8FD8\u6CA1\u8FD0\u884C\u65F6\u5C31\u80FD\u591F\u53D1\u73B0\u5176\u4E2D\u9690\u85CF\u7684\u9519\u8BEF\u3002\u7A33\u5B9A\u6027\u3001\u4EE3\u7801\u8D28\u91CF\u3001\u4E25\u8C28\u6027\u3001\u53EF\u8BFB\u6027\u7B49\u7B49\uFF0C\u5176\u5B9E\u672C\u8D28\u90FD\u662F\u8FD9\u4E00\u80FD\u529B\u3002\u5B83\u4EEC\u7684\u786E\u4E0D\u80FD\u8BA9\u4F60\u7684\u9875\u9762\u6027\u80FD\u66F4\u597D\u6216\u662F\u4E1A\u52A1\u6570\u636E\u66F4\u68D2\uFF0C\u4F46\u5374\u80FD\u907F\u514D\u6F5C\u5728\u7684\u4E25\u91CD\u95EE\u9898\uFF0C\u6BD4\u5982\u767D\u5C4F\u548C\u585E\u6EE1\u9519\u8BEF\u76D1\u63A7\u7684 <code>Cannot read property of undefined</code>\u3002</p><p>\u5BF9\u4E8E TypeScript \u6765\u8BF4\u6700\u91CD\u8981\u7684\uFF0C\u5176\u5B9E\u662F\u4E0E JavaScript \u7684\u7D27\u5BC6\u7ED3\u5408\uFF0C\u7C7B\u578B\u80FD\u529B\u662F\u6DFB\u52A0\u5728 JavaScript \u4E4B\u4E0A\u7684\uFF0C\u4E5F\u5C31\u610F\u5473\u7740\u6211\u4EEC\u4E0D\u9700\u8981\u5B66\u4E60\u4E00\u95E8\u5168\u65B0\u7684\u7F16\u7A0B\u8BED\u8A00\u6765\u83B7\u5F97\u7C7B\u578B\u68C0\u67E5\u3002\u540C\u65F6\uFF0C\u6211\u4EEC\u53EF\u4EE5\u8BF4\u5B83\u7684\u5B66\u4E60\u66F2\u7EBF\u662F\u76F8\u5BF9\u5E73\u7F13\u7684\uFF0C\u56E0\u4E3A\u5E76\u4E0D\u9700\u8981\u5B66\u4E60\u592A\u957F\u65F6\u95F4\u5C31\u80FD\u5199\u51FA\u8FD8\u8FC7\u5F97\u53BB\u7684 TypeScript \u4EE3\u7801\u3002\u4F46\u5B83\u7684\u8FB9\u9645\u6210\u672C\u662F\u660E\u663E\u9012\u589E\u7684\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5047\u5982\u4F60\u6700\u5F00\u59CB\u53EA\u9700\u8981\u82B1 3 \u4E2A\u5355\u4F4D\u7684\u7CBE\u529B\u548C\u65F6\u95F4\uFF0C\u5C31\u80FD\u63D0\u5347 10 \u4E2A\u5355\u4F4D\u7684\u7C7B\u578B\u8986\u76D6\u7A0B\u5EA6\uFF08\u7528\u6765\u8861\u91CF TypeScript \u4EE3\u7801\u8D28\u91CF\u7684\u6307\u6807\u4E4B\u4E00\uFF09\uFF0C\u540E\u9762\u53EF\u80FD\u9700\u8981\u82B1 10 \u4E2A\u5355\u4F4D\u7684\u7CBE\u529B\u548C\u65F6\u95F4\u624D\u80FD\u518D\u63D0\u5347 1 \u4E2A\u5355\u4F4D\u7684\u7C7B\u578B\u8986\u76D6\u3002</p><p>\u4F46\u4F60\u771F\u7684\u9700\u8981\u5982\u6B64\u9AD8\u7684\u7C7B\u578B\u8986\u76D6\u7A0B\u5EA6\uFF0C\u4EE5\u53CA\u613F\u610F\u82B1\u5982\u6B64\u591A\u7684\u7CBE\u529B\u65F6\u95F4\u5417\uFF1F\u6211\u7684\u5EFA\u8BAE\u662F\uFF0C\u5728\u4F60\u4F18\u5316 TypeScript \u4EE3\u7801\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u5982\u679C\u611F\u5230\u8FB9\u9645\u6210\u672C\u660E\u663E\u63D0\u5347\uFF0C\u91CD\u6784\u5DE5\u4F5C\u5F00\u59CB\u6709\u4E9B\u5403\u529B\uFF0C\u90A3\u4F60\u5F88\u53EF\u80FD\u5DF2\u7ECF\u8FBE\u5230\u4E86\u4E00\u4E2A\u4E34\u754C\u503C\uFF0C\u4E5F\u662F\u8BE5\u505C\u4E0B\u6765\u7684\u65F6\u5019\u4E86\u3002</p><p>\u800C\u5BF9\u4E8E TypeScript \u7684\u5B66\u4E60\u66F2\u7EBF\uFF0C\u81EA\u7136\u4E5F\u5B58\u5728\u7740\u8FD9\u4E48\u4E00\u4E2A\u5E73\u8861\u70B9\uFF0C\u5B83\u6807\u5FD7\u7740\u4F60\u7684\u6295\u5165\u7CBE\u529B\u548C\u83B7\u5F97\u56DE\u62A5\u8FBE\u6210\u4E86\u76F8\u5BF9\u7684\u5E73\u8861\uFF0C\u518D\u7EE7\u7EED\u6295\u5165\u66F4\u591A\u7CBE\u529B\u4E5F\u65E0\u6CD5\u770B\u5230\u660E\u663E\u7684\u6548\u679C\u3002\u4F60\u662F\u5426\u8981\u95EE\uFF0C\u8FD9\u672C\u5C0F\u518C\u4E2D\u662F\u5426\u4E5F\u5B58\u5728\u7740\u8FD9\u4E48\u4E2A\u5E73\u8861\u70B9\uFF1F\u5F53\u7136\u4E5F\u662F\uFF0C\u53EA\u4E0D\u8FC7\u662F\u51E0\u4E4E\u5168\u672C\u7684\u5185\u5BB9\uFF08\u7B11\uFF09\u3002\u76F8\u4FE1\u6211\uFF0C\u8FD9\u672C\u5C0F\u518C\u4E2D\u57FA\u672C\u90FD\u662F\u4F60\u73B0\u5728\u6216\u672A\u6765\u4F1A\u9700\u8981\u7684\u6280\u80FD\u70B9\u3002\u53EA\u6709 Compiler API \u90E8\u5206\u7A0D\u663E\u590D\u6742\uFF0C\u4F46\u6211\u4EEC\u4E5F\u4F1A\u8BB2\u5F97\u6709\u8DA3\u597D\u73A9\u4E00\u4E9B\u3002\u800C\u5BF9\u4F60\u81EA\u5DF1\u6765\u8BF4\uFF0C\u8FD9\u4E2A\u5E73\u8861\u70B9\u5176\u5B9E\u53EF\u4EE5\u975E\u5E38\u7075\u6D3B\uFF0C\u968F\u7740\u4F60\u7684\u5B66\u4E60\u6DF1\u5165\uFF0CTypeScript \u6280\u80FD\u4E0D\u65AD\u63D0\u5347\uFF0C\u5C06\u4F1A\u8D8A\u6765\u8D8A\u6E34\u671B\u66F4\u591A\u7684\u77E5\u8BC6\uFF0C\u90A3\u5E73\u8861\u70B9\u4E0D\u5C31\u8D8A\u6765\u8D8A\u9AD8\u4E86\uFF1F</p><p>\u65E0\u8BBA\u5982\u4F55\u8BB0\u4F4F\u4E00\u4EF6\u4E8B\uFF0C\u4E0D\u8981\u903C\u81EA\u5DF1\u5B66\u4E60\uFF0C\u5F53\u4F60\u5728\u67D0\u4E00\u8282\u5B66\u4E60\u8FC7\u7A0B\u4E2D\u611F\u89C9\u5230\u5BB9\u6613\u5206\u795E\u65F6\uFF0C\u90A3\u5C31\u662F\u65F6\u5019\u4F11\u606F\u4E86\uFF0C\u7AD9\u8D77\u6765\u6D3B\u52A8\u4E0B\u8EAB\u4F53\uFF0C\u6253\u5F00\u7A97\u6237\u547C\u5438\u4E0B\u65B0\u9C9C\u7A7A\u6C14\uFF0C\u6211\u4F1A\u4E00\u76F4\u5728\u8FD9\u91CC\u7B49\u4F60\u56DE\u6765\u3002</p><h2 id="type-challenges" tabindex="-1">Type Challenges <a class="header-anchor" href="#type-challenges" aria-hidden="true">#</a></h2><p>\u6700\u540E\uFF0C\u6211\u4EEC\u518D\u6765\u8BF4\u4E0B\u5982\u4F55\u8FDB\u4E00\u6B65\u8FDB\u9636\u7C7B\u578B\u7F16\u7A0B\u80FD\u529B\u3002<a href="https://link.juejin.cn/?target=https%3A%2F%2Fgithub.com%2Ftype-challenges%2Ftype-challenges" target="_blank" rel="noreferrer">Type Challenge </a>\u662F antfu \uFF08Vue \u56E2\u961F\u6210\u5458\uFF0C\u4EE5\u53CA Vite\u3001Vitest\u3001Nuxt \u7B49\u77E5\u540D\u5F00\u6E90\u9879\u76EE\u7684\u56E2\u961F\u6210\u5458\u6216\u4F5C\u8005\uFF09\u7684\u4F5C\u54C1\uFF0C\u5176\u4E2D\u641C\u96C6\u4E86\u8BB8\u591A\u7C7B\u578B\u7F16\u7A0B\u7684\u9898\u76EE\uFF0C\u5E76\u4E14\u8D34\u5FC3\u5730\u6309\u7167\u96BE\u6613\u7A0B\u5EA6\u5206\u4E3A\u4E86 easy\u3001medium\u3001hard \u4E09\u4E2A\u7B49\u7EA7\u3002</p><p>\u867D\u7136\u6211\u5E76\u6CA1\u6709\u628A\u6BCF\u4E00\u9053\u9898\u90FD\u5B8C\u6210\uFF0C\u4F46\u6211\u4ECD\u7136\u63A8\u8350\u60F3\u8981\u5B66\u4E60\u66F4\u9AD8\u9636\u7C7B\u578B\u7F16\u7A0B\u7684\u540C\u5B66\u53BB\u82B1\u4E0A\u4E00\u5B9A\u65F6\u95F4\u770B\u770B\u3002\u5F53\u7136\uFF0C\u524D\u63D0\u662F\u4F60\u5DF2\u7ECF\u5BF9\u6574\u4E2A\u7C7B\u578B\u80FD\u529B\u6709\u6BD4\u8F83\u6E05\u6670\u8BA4\u77E5\u7684\u60C5\u51B5\u4E0B\uFF0C\u53EA\u6709\u6B64\u65F6 Type Challenge \u624D\u80FD\u4E3A\u4F60\u5E26\u6765\u6B63\u9762\u4F5C\u7528\u3002\u8FD9\u672C\u5C0F\u518C\u4E2D\u5E76\u4E0D\u4F1A\u6709\u8FC7\u4E8E\u590D\u6742\u7684\u5DE5\u5177\u7C7B\u578B\u8BB2\u89E3\uFF0C\u5982\u679C\u4F60\u5B66\u5B8C\u4E4B\u540E\u611F\u89C9\u610F\u72B9\u672A\u5C3D\uFF0C\u8FD8\u60F3\u5C1D\u8BD5\u66F4\u591A\u590D\u6742\u7684\u7C7B\u578B\u7F16\u7A0B\uFF0C\u6B64\u65F6\u5C31\u53EF\u4EE5\u6253\u5F00\u5B83\u6765\u4E0A\u90A3\u4E48\u51E0\u9898\u4E86\u3002</p><p>\u8BF7\u52A1\u5FC5\u7262\u8BB0\uFF0CType Challenge \u662F\u5728\u5DF2\u6709\u4E00\u5B9A\u7C7B\u578B\u7F16\u7A0B\u57FA\u7840\u540E\uFF0C\u7528\u6765\u8FDB\u4E00\u6B65\u63D0\u9AD8\u6C34\u5E73\u7684\u65B9\u5F0F\uFF0C\u800C\u4E0D\u662F\u4F60\u7528\u6765\u5B66\u4E60\u7C7B\u578B\u7F16\u7A0B\u4E0E\u7C7B\u578B\u7CFB\u7EDF\u7684\u65B9\u5F0F\uFF0C\u52A1\u5FC5\u5206\u6E05\u5176\u4E2D\u7684\u56E0\u679C\u5173\u7CFB\u3002</p>',19),l=[i];function n(c,_,s,o,h,d){return t(),p("div",null,l)}const g=e(a,[["render",n]]);export{T as __pageData,g as default};
